  }
});

  // Consultation endpoints
  app.post('/api/consultations', async (req, res) => {
    try {
      const consultation = await storage.createConsultation(req.body);
      res.json(consultation);
    } catch (error) {
      console.error('Error creating consultation:', error);
      res.status(500).json({ message: 'Failed to create consultation' });
    }
  });

  app.get('/api/consultations/patient/:patientId', async (req, res) => {
    try {
      const consultation = await storage.getConsultationByPatient(req.params.patientId);
      res.json(consultation);
    } catch (error) {
      console.error('Error fetching consultation:', error);
      res.status(500).json({ message: 'Failed to fetch consultation' });
    }
  });

  // Services endpoints
  app.post('/api/services', async (req, res) => {
    try {
      const service = await storage.createService(req.body);
      res.json(service);
    } catch (error) {
      console.error('Error creating service:', error);
      res.status(500).json({ message: 'Failed to create service' });
    }
  });

  app.get('/api/services/patient/:patientId', async (req, res) => {
    try {
      const services = await storage.getServicesByPatient(req.params.patientId);
      res.json(services);
    } catch (error) {
      console.error('Error fetching services:', error);
      res.status(500).json({ message: 'Failed to fetch services' });
    }
  });

  app.get('/api/services/patient/:patientId/:type', async (req, res) => {
    try {
      const { patientId, type } = req.params;
      const services = await storage.getActiveServicesByType(patientId, type as 'lab' | 'pharmacy');
      res.json(services);
    } catch (error) {
      console.error('Error fetching services by type:', error);
      res.status(500).json({ message: 'Failed to fetch services' });
    }
  });

  // Lab order cancellation endpoint
  app.post('/api/lab-orders/cancel', async (req, res) => {
    try {
      const { labId, doctorId, reason } = req.body;

      if (!labId || !doctorId || !reason) {
        return res.status(400).json({ message: 'Missing required fields: labId, doctorId, and reason' });
      }

      const cancelledOrder = await storage.cancelLabOrder(labId, doctorId, reason);

      if (!cancelledOrder) {
        return res.status(404).json({ message: 'Lab order not found or could not be cancelled' });
      }

      res.json({
        message: 'Lab order cancelled successfully',
        order: cancelledOrder
      });
    } catch (error) {
      console.error('Error cancelling lab order:', error);
      res.status(500).json({ message: 'Failed to cancel lab order' });
    }
  });

  // Create review consultation
  app.post('/api/consultations/review', async (req, res) => {
    try {
      const { patientId, doctorId, reason, cancelledLabId, originalService } = req.body;

      if (!patientId || !doctorId) {
        return res.status(400).json({ message: 'Missing required fields: patientId and doctorId' });
