      <Route path="/debtors-dashboard" component={DebtorsDashboard} />
      <Route path="/simple-debtors-dashboard" component={SimpleDebtorsDashboard} />
      <Route path="/verification-audit" component={DebtorsVerificationAudit} />

      {/* Legacy redirects */}
      <ProtectedRoute path="/pharmacy-dashboard" component={ModernPharmacyDashboard} />
      <ProtectedRoute path="/doctor-consultation" component={DoctorConsultation} />

      {/* Focused Claims Interface */}
      <Route path="/focused-claims" component={FocusedClaims} />

      {/* Biometric Demo */}
      <ProtectedRoute path="/biometric-demo" component={BiometricDemo} />

      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <AuthProvider>
        <TooltipProvider>
          <Toaster />
          <SessionTimeoutWarning />
          <Router />
        </TooltipProvider>
      </AuthProvider>
    </QueryClientProvider>
  );
}

export default App;

