}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground font-sans antialiased;
  }
}

@layer components {
  .medical-card {
    @apply bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow duration-200;
  }

  .teal-button {
    @apply bg-teal-primary text-white hover:bg-teal-dark focus:ring-2 focus:ring-teal-primary focus:ring-offset-2 transition-colors duration-200;
  }

  .ai-indicator {
    @apply inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-700;
  }

  .status-approved {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800;
  }

  .status-pending {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800;
  }

  .status-denied {
    @apply inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800;
  }

  .biometric-scanner {
    @apply relative w-24 h-24 mx-auto bg-gradient-to-br from-teal-primary to-teal-secondary rounded-full flex items-center justify-center shadow-lg;
  }

  .fingerprint-icon {
    @apply text-white text-3xl drop-shadow-sm;
  }

  .blockchain-hash {
    @apply font-mono text-xs bg-gray-100 dark:bg-gray-800 px-2 py-1 rounded border text-gray-600 dark:text-gray-400 break-all;
  }

  .medical-form-input {
    @apply w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-primary focus:border-transparent transition-all duration-200;
  }

  .nav-item {
    @apply flex items-center space-x-3 px-4 py-3 text-gray-600 hover:text-teal-primary hover:bg-teal-50 rounded-lg transition-colors duration-200 cursor-pointer;
  }

  .nav-item.active {
    @apply text-teal-primary bg-teal-50 font-medium;
  }
}

/* Custom animations for healthcare UI */
@keyframes pulse-slow {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

@keyframes scan-line {
  0% {
    transform: translateY(-100%);
  }
  100% {
    transform: translateY(100%);
  }
}

.animate-pulse-slow {
  animation: pulse-slow 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

.animate-scan {
  animation: scan-line 2s ease-in-out infinite;
}

/* Medical chart styles */
.medical-chart {
  @apply bg-white rounded-lg border border-gray-200 p-4;
}

/* Accessibility improvements */
@media (prefers-reduced-motion: reduce) {
  .animate-pulse-slow,
  .animate-scan,
  .transition-all,
  .transition-colors,
  .transition-shadow {
    animation: none;
    transition: none;
  }
}

/* Print styles for medical documents */
@media print {
  .no-print {
    display: none !important;
  }

  .medical-card {
    @apply shadow-none border border-gray-400;
  }
}

