import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Progress } from '@/components/ui/progress';
import { Fingerprint, CheckCircle, AlertCircle, Loader2 } from 'lucide-react';
import { cn } from '@/lib/utils';

interface BiometricScannerProps {
  mode: 'register' | 'verify';
  patientId: string;
  onSuccess?: (data: any) => void;
  onError?: (error: string) => void;
  className?: string;
}

export function BiometricScanner({ 
  mode, 
  patientId, 
  onSuccess, 
  onError, 
  className 
}: BiometricScannerProps) {
  const [scanning, setScanning] = useState(false);
  const [scanProgress, setScanProgress] = useState(0);
  const [scanResult, setScanResult] = useState<'success' | 'failed' | null>(null);
  const [errorMessage, setErrorMessage] = useState('');
  const [verificationScore, setVerificationScore] = useState(0);

  // Simulate fingerprint scanning with progressive updates
  const simulateFingerprint = async (): Promise<string> => {
    setScanning(true);
    setScanProgress(0);
    setScanResult(null);
    setErrorMessage('');

    // Progressive scanning simulation
    const steps = [
      { progress: 15, message: 'Initializing scanner...' },
      { progress: 30, message: 'Detecting finger placement...' },
      { progress: 50, message: 'Capturing ridge patterns...' },
      { progress: 70, message: 'Analyzing minutiae points...' },
      { progress: 85, message: 'Generating biometric hash...' },
      { progress: 100, message: 'Scan complete!' }
    ];

    for (const step of steps) {
      await new Promise(resolve => setTimeout(resolve, 300 + Math.random() * 200));
      setScanProgress(step.progress);
    }

    // Generate mock fingerprint data (base64 encoded)
    const timestamp = Date.now();
    const randomData = Array.from({length: 64}, () => 
      Math.floor(Math.random() * 256).toString(16).padStart(2, '0')
    ).join('');

    return btoa(`fingerprint_${patientId}_${timestamp}_${randomData}`);
  };

  const handleScan = async () => {
    try {
      const fingerprintData = await simulateFingerprint();

      const endpoint = mode === 'register' 
        ? '/api/biometric/register'
        : '/api/biometric/verify';

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        body: JSON.stringify({
          patientId,
          fingerprintData,
          deviceId: `web_${Date.now()}`
        })
      });

      const result = await response.json();

      if (result.success) {
        setScanResult('success');
        if (mode === 'verify' && result.verificationScore) {
          setVerificationScore(result.verificationScore);
        }
        onSuccess?.(result);
      } else {
        setScanResult('failed');
        setErrorMessage(result.error || 'Biometric operation failed');
        onError?.(result.error || 'Biometric operation failed');
      }
    } catch (error) {
      setScanResult('failed');
      const errorMsg = error instanceof Error ? error.message : 'Network error occurred';
      setErrorMessage(errorMsg);
      onError?.(errorMsg);
    } finally {
      setScanning(false);
    }
  };

  return (
    <Card className={cn("w-full max-w-md mx-auto", className)}>
      <CardHeader className="text-center pb-4">
        <CardTitle className="flex items-center justify-center gap-2">
          <Fingerprint className="h-6 w-6 text-[#265651]" />
          {mode === 'register' ? 'Register Fingerprint' : 'Verify Identity'}
        </CardTitle>
        <Badge variant="outline" className="mx-auto">
          Patient ID: {patientId}
        </Badge>
      </CardHeader>

      <CardContent className="space-y-6">
        {/* Scanner Visualization */}
        <div className="relative">
          <div className={cn(
            "w-32 h-32 mx-auto rounded-full border-4 flex items-center justify-center transition-all duration-300",
            scanning ? "border-[#265651] bg-[#265651]/10 animate-pulse" : "border-gray-300",
            scanResult === 'success' && "border-green-500 bg-green-50",
            scanResult === 'failed' && "border-red-500 bg-red-50"
          )}>
            {scanning ? (
              <Loader2 className="h-12 w-12 text-[#265651] animate-spin" />
            ) : scanResult === 'success' ? (
              <CheckCircle className="h-12 w-12 text-green-500" />
            ) : scanResult === 'failed' ? (
              <AlertCircle className="h-12 w-12 text-red-500" />
            ) : (
              <Fingerprint className="h-12 w-12 text-gray-400" />
            )}
          </div>

          {/* Progress Ring */}
          {scanning && (
            <div className="absolute inset-0 flex items-center justify-center">
              <svg className="w-36 h-36 transform -rotate-90">
                <circle
                  cx="72"
                  cy="72"
                  r="68"
                  stroke="#e5e7eb"
                  strokeWidth="4"
                  fill="transparent"
                />
                <circle
                  cx="72"
                  cy="72"
                  r="68"
                  stroke="#265651"
                  strokeWidth="4"
                  fill="transparent"
                  strokeDasharray={427}
                  strokeDashoffset={427 - (427 * scanProgress) / 100}
                  className="transition-all duration-300 ease-out"
                />
              </svg>
            </div>
          )}
        </div>

        {/* Progress Bar */}
        {scanning && (
          <div className="space-y-2">
            <Progress value={scanProgress} className="w-full" />
            <p className="text-sm text-center text-gray-600">
              {scanProgress < 100 ? 'Scanning...' : 'Processing...'}
            </p>
          </div>
        )}

        {/* Verification Score */}
        {scanResult === 'success' && mode === 'verify' && verificationScore > 0 && (
          <Alert className="border-green-200 bg-green-50">
            <CheckCircle className="h-4 w-4" />
            <AlertDescription>
              Verification successful! Match confidence: {Math.round(verificationScore)}%
            </AlertDescription>
          </Alert>
        )}

        {/* Error Message */}
        {scanResult === 'failed' && errorMessage && (
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              {errorMessage}
            </AlertDescription>
          </Alert>
        )}

        {/* Action Buttons */}
        <div className="space-y-3">
          <Button
            onClick={handleScan}
            disabled={scanning}
            className="w-full bg-[#265651] hover:bg-[#1d453f] text-white"
          >
            {scanning ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Scanning...
              </>
            ) : (
              <>
                <Fingerprint className="mr-2 h-4 w-4" />
                {mode === 'register' ? 'Register Fingerprint' : 'Verify Fingerprint'}
              </>
            )}
          </Button>
        </div>

        {/* Instructions */}
        <div className="text-sm text-gray-600 text-center space-y-2">
          <p>
            {mode === 'register' 
              ? 'Place your finger on the scanner to register'
              : 'Verify your identity using the same finger used during registration'
            }
          </p>
          <p className="text-xs text-gray-500">
            Ensure your finger is clean and dry for best results
          </p>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/BiometricVerificat.content:
import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { BiometricScanner } from './BiometricScanner';
import { 
  User, 
  Fingerprint, 
  CheckCircle, 
  AlertTriangle, 
  ArrowRight,
  Shield,
  Clock,
  UserCheck
} from 'lucide-react';
import { cn } from '@/lib/utils';
import { useQuery } from '@tanstack/react-query';
import { useLocation } from 'wouter';

interface BiometricVerificationFlowProps {
  patientId: string;
  patientName?: string;
  onVerificationSuccess?: (result: any) => void;
  onRegistrationComplete?: (result: any) => void;
  redirectTo?: string;
  className?: string;
}

interface BiometricInfo {
  info: {
    patientId: string;
    fingerprintHash: string;
    registeredBy: string;
    registeredAt: string;
    status: 'active' | 'archived' | 'pending_reset';
  } | null;
  message?: string;
}

export function BiometricVerificationFlow({
  patientId,
  patientName,
  onVerificationSuccess,
  onRegistrationComplete,
  redirectTo,
  className
}: BiometricVerificationFlowProps) {
  const [step, setStep] = useState<'checking' | 'register' | 'verify' | 'success'>('checking');
  const [showRegistration, setShowRegistration] = useState(false);
  const [verificationResult, setVerificationResult] = useState<any>(null);
  const [, setLocation] = useLocation();

  // Check if fingerprint exists for patient
  const { data: biometricInfo, isLoading } = useQuery<BiometricInfo>({
    queryKey: [`/api/biometric/info/${patientId}`],
    enabled: !!patientId
  });

  useEffect(() => {
    if (!isLoading && biometricInfo) {
      if (biometricInfo.info && biometricInfo.info.status === 'active') {
        setStep('verify');
      } else {
        setStep('register');
      }
    }
  }, [biometricInfo, isLoading]);

  const handleRegistrationSuccess = (result: any) => {
    setStep('verify');
    setShowRegistration(false);
    onRegistrationComplete?.(result);
  };

  const handleVerificationSuccess = (result: any) => {
    setVerificationResult(result);
    setStep('success');
    onVerificationSuccess?.(result);
  };

  const handleProceed = () => {
    if (redirectTo) {
      setLocation(redirectTo);
    }
  };

  const renderStepIndicator = () => {
    const steps = [
      { id: 'checking', label: 'Checking', icon: User },
      { id: 'register', label: 'Register', icon: Fingerprint },
      { id: 'verify', label: 'Verify', icon: Shield },
      { id: 'success', label: 'Complete', icon: CheckCircle }
    ];

    return (
      <div className="flex items-center justify-center mb-6">
        {steps.map((stepItem, index) => {
          const Icon = stepItem.icon;
          const isActive = step === stepItem.id;
          const isCompleted = ['register', 'verify', 'success'].indexOf(step) > 
                           ['register', 'verify', 'success'].indexOf(stepItem.id);

          return (
            <div key={stepItem.id} className="flex items-center">
              <div className={cn(
                "flex items-center justify-center w-10 h-10 rounded-full border-2 transition-colors",
                isActive && "border-[#265651] bg-[#265651] text-white",
                isCompleted && "border-green-500 bg-green-500 text-white",
                !isActive && !isCompleted && "border-gray-300 text-gray-400"
              )}>
                <Icon className="w-5 h-5" />
              </div>
              {index < steps.length - 1 && (
                <div className={cn(
                  "w-12 h-px mx-2 transition-colors",
                  isCompleted && "bg-green-500",
                  !isCompleted && "bg-gray-300"
                )} />
              )}
            </div>
          );
        })}
      </div>
    );
  };

  if (isLoading) {
    return (
      <Card className={cn("w-full max-w-lg mx-auto", className)}>
        <CardContent className="flex items-center justify-center py-12">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-[#265651]"></div>
          <span className="ml-3 text-gray-600">Checking biometric status...</span>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className={cn("w-full max-w-2xl mx-auto space-y-6", className)}>
      {renderStepIndicator()}

      {/* Patient Information Card */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <UserCheck className="h-5 w-5 text-[#265651]" />
            Patient Verification
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            <div className="flex justify-between items-center">
              <span className="font-medium">Patient ID:</span>
              <Badge variant="outline">{patientId}</Badge>
            </div>
            {patientName && (
              <div className="flex justify-between items-center">
                <span className="font-medium">Patient Name:</span>
                <span>{patientName}</span>
              </div>
            )}
            <div className="flex justify-between items-center">
              <span className="font-medium">Biometric Status:</span>
              <Badge variant={biometricInfo?.info ? 'default' : 'secondary'}>
                {biometricInfo?.info ? 'Registered' : 'Not Registered'}
              </Badge>
            </div>
            {biometricInfo?.info && (
              <div className="flex justify-between items-center">
                <span className="font-medium">Registered:</span>
                <span className="text-sm text-gray-600 flex items-center gap-1">
                  <Clock className="h-4 w-4" />
                  {new Date(biometricInfo.info.registeredAt).toLocaleDateString()}
                </span>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Main Flow Card */}
      <Card>
        <CardContent className="pt-6">
          {step === 'register' && (
            <div className="text-center space-y-6">
              <Alert className="border-orange-200 bg-orange-50">
                <AlertTriangle className="h-4 w-4" />
                <AlertDescription>
                  <strong>No biometric data found.</strong> Please register fingerprint to proceed with secure verification.
                </AlertDescription>
              </Alert>

              <div className="space-y-4">
                <h3 className="text-lg font-semibold">Fingerprint Registration Required</h3>
                <p className="text-gray-600">
                  To ensure secure patient identity verification, we need to register your fingerprint. 
                  This is a one-time setup that will enable quick verification for future visits.
                </p>

                <Button 
                  onClick={() => setShowRegistration(true)}
                  className="bg-[#265651] hover:bg-[#1d453f] text-white"
                >
                  <Fingerprint className="mr-2 h-4 w-4" />
                  Register Fingerprint
                </Button>
              </div>
            </div>
          )}

          {step === 'verify' && (
            <div className="space-y-6">
              <div className="text-center">
                <h3 className="text-lg font-semibold mb-2">Identity Verification</h3>
                <p className="text-gray-600">
                  Place your finger on the scanner to verify your identity and proceed.
                </p>
              </div>

              <BiometricScanner
                mode="verify"
                patientId={patientId}
                onSuccess={handleVerificationSuccess}
                onError={(error) => console.error('Verification failed:', error)}
              />
            </div>
          )}

          {step === 'success' && (
            <div className="text-center space-y-6">
              <div className="w-16 h-16 mx-auto bg-green-100 rounded-full flex items-center justify-center">
                <CheckCircle className="h-8 w-8 text-green-600" />
              </div>

              <div>
                <h3 className="text-lg font-semibold text-green-800">Verification Successful!</h3>
                <p className="text-gray-600 mt-2">
                  Identity confirmed. You can now proceed to your requested service.
                </p>
                {verificationResult?.verificationScore && (
                  <Badge variant="outline" className="mt-3">
                    Match: {Math.round(verificationResult.verificationScore)}%
                  </Badge>
                )}
              </div>

              <div className="space-y-3">
                {redirectTo && (
                  <Button 
                    onClick={handleProceed}
                    className="w-full bg-[#265651] hover:bg-[#1d453f] text-white"
                  >
                    <ArrowRight className="mr-2 h-4 w-4" />
                    Proceed to {redirectTo.includes('pharmacy') ? 'Pharmacy' : 'Front Desk'}
                  </Button>
                )}

                <div className="flex gap-3 justify-center">
                  <Button 
                    variant="outline"
                    onClick={() => setLocation('/modern-pharmacy')}
                  >
                    Pharmacy
                  </Button>
                  <Button 
                    variant="outline"
                    onClick={() => setLocation('/front-desk')}
                  >
                    Front Desk
                  </Button>
                </div>
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Registration Dialog */}
      <Dialog open={showRegistration} onOpenChange={setShowRegistration}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Register Fingerprint</DialogTitle>
            <DialogDescription>
              This will securely register your fingerprint for future identity verification.
            </DialogDescription>
          </DialogHeader>

          <BiometricScanner
            mode="register"
            patientId={patientId}
            onSuccess={handleRegistrationSuccess}
            onError={(error) => console.error('Registration failed:', error)}
          />
        </DialogContent>
      </Dialog>
    </div>
  );
}

