client/src/components/active-lab-orders..content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { 
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { useToast } from "@/hooks/use-toast";
import { 
  TestTube, 
  Clock, 
  Calendar,
  X,
  AlertTriangle
} from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

interface LabOrder {
  id: number;
  serviceName: string;
  serviceCode?: string;
  instructions: string;
  durationDays: number;
  expiresAt: string;
  createdAt: string;
  status: string;
}

interface ActiveLabOrdersProps {
  patientId: string;
  doctorId: number;
}

export default function ActiveLabOrders({ patientId, doctorId }: ActiveLabOrdersProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [cancelDialog, setCancelDialog] = useState<{ open: boolean; labId?: number }>({ open: false });
  const [cancelReason, setCancelReason] = useState("");
  const [reviewConsultationDialog, setReviewConsultationDialog] = useState(false);
  const [cancelledOrderDetails, setCancelledOrderDetails] = useState<any>(null);

  // Fetch active lab orders
  const { data: labOrders = [], isLoading } = useQuery<LabOrder[]>({
    queryKey: [`/api/services/patient/${patientId}/lab`],
    enabled: !!patientId
  });

  // Cancel lab order mutation
  const cancelMutation = useMutation({
    mutationFn: async ({ labId, reason }: { labId: number; reason: string }) => {
      return await apiRequest('/api/lab-orders/cancel', 'POST', {
        labId,
        doctorId,
        reason
      });
    },
    onSuccess: (data) => {
      toast({
        title: "Lab Order Cancelled",
        description: "The lab order has been successfully cancelled.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/services/patient/${patientId}/lab`] });
      setCancelDialog({ open: false });
      setCancelReason("");

      // Store cancelled order details and show review consultation prompt
      setCancelledOrderDetails(data.order);
      setReviewConsultationDialog(true);
    },
    onError: (error) => {
      toast({
        title: "Cancellation Failed",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  // Create review consultation mutation
  const reviewConsultationMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest('/api/consultations/review', 'POST', {
        patientId,
        doctorId,
        reason: "Review consultation for cancelled lab order",
        cancelledLabId: cancelledOrderDetails?.id,
        originalService: cancelledOrderDetails?.serviceName
      });
    },
    onSuccess: (data) => {
      toast({
        title: "Review Consultation Created",
        description: "A new review consultation has been initiated for this patient.",
      });
      setReviewConsultationDialog(false);
      setCancelledOrderDetails(null);

      // Navigate to the new consultation
      window.location.href = `/doctor-consultation?patientId=${patientId}&consultationId=${data.id}`;
    },
    onError: (error) => {
      toast({
        title: "Failed to Create Review Consultation",
        description: error.message,
        variant: "destructive",
      });
    }
  });

  const handleCancelClick = (labId: number) => {
    setCancelDialog({ open: true, labId });
  };

  const handleConfirmCancel = () => {
    if (!cancelDialog.labId || !cancelReason.trim()) {
      toast({
        title: "Justification Required",
        description: "Please provide a reason for cancelling this lab order.",
        variant: "destructive",
      });
      return;
    }

    cancelMutation.mutate({
      labId: cancelDialog.labId,
      reason: cancelReason.trim()
    });
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getDaysRemaining = (expiresAt: string) => {
    const expiry = new Date(expiresAt);
    const today = new Date();
    const diffTime = expiry.getTime() - today.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <TestTube className="h-5 w-5" />
            <span>Active Lab Orders</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center justify-center py-8">
            <div className="w-6 h-6 border-2 border-teal-500 border-t-transparent rounded-full animate-spin"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <TestTube className="h-5 w-5" />
            <span>Active Lab Orders</span>
          </CardTitle>
          <CardDescription>
            Current active lab orders for this patient
          </CardDescription>
        </CardHeader>
        <CardContent>
          {labOrders.length === 0 ? (
            <p className="text-gray-500 text-center py-8">No active lab orders</p>
          ) : (
            <div className="space-y-4">
              {labOrders.map((order) => {
                const daysRemaining = getDaysRemaining(order.expiresAt);
                const isExpiringSoon = daysRemaining <= 30;
                const isExpired = daysRemaining <= 0;

                return (
                  <div key={order.id} className="border rounded-lg p-4 space-y-3">
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <div className="flex items-center space-x-2 mb-2">
                          <h4 className="font-medium text-lg">{order.serviceName}</h4>
                          {order.serviceCode && (
                            <Badge variant="outline">{order.serviceCode}</Badge>
                          )}
                          <Badge 
                            variant={isExpired ? "destructive" : isExpiringSoon ? "secondary" : "default"}
                          >
                            {order.status}
                          </Badge>
                        </div>

                        {order.instructions && (
                          <p className="text-gray-600 mb-2">
                            <strong>Instructions:</strong> {order.instructions}
                          </p>
                        )}

                        <div className="flex items-center space-x-4 text-sm text-gray-500">
                          <div className="flex items-center space-x-1">
                            <Calendar className="h-4 w-4" />
                            <span>Ordered: {formatDate(order.createdAt)}</span>
                          </div>

                          <div className="flex items-center space-x-1">
                            <Clock className="h-4 w-4" />
                            <span 
                              className={
                                isExpired 
                                  ? "text-red-600 font-medium" 
                                  : isExpiringSoon 
                                    ? "text-orange-600 font-medium" 
                                    : ""
                              }
                            >
                              {isExpired 
                                ? "Expired" 
                                : `${daysRemaining} days remaining`
                              }
                            </span>
                          </div>
                        </div>
                      </div>

                      <Button
                        onClick={() => handleCancelClick(order.id)}
                        variant="outline"
                        size="sm"
                        className="text-red-600 hover:text-red-700 hover:bg-red-50"
                        disabled={cancelMutation.isPending}
                      >
                        <X className="h-4 w-4 mr-1" />
                        Cancel
                      </Button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Cancel Confirmation Dialog */}
      <Dialog open={cancelDialog.open} onOpenChange={(open) => setCancelDialog({ open })}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle className="flex items-center space-x-2">
              <AlertTriangle className="h-5 w-5 text-orange-500" />
              <span>Cancel Lab Order</span>
            </DialogTitle>
            <DialogDescription>
              Please provide a justification for cancelling this lab order. This action cannot be undone.
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <Label htmlFor="cancelReason">Justification for Cancellation *</Label>
              <Textarea
                id="cancelReason"
                placeholder="Enter reason for cancelling this lab order..."
                value={cancelReason}
                onChange={(e) => setCancelReason(e.target.value)}
                className="min-h-[100px]"
              />
            </div>
          </div>

          <DialogFooter>
            <Button 
              variant="outline" 
              onClick={() => setCancelDialog({ open: false })}
              disabled={cancelMutation.isPending}
            >
              Keep Order
            </Button>
            <Button
              onClick={handleConfirmCancel}
              disabled={cancelMutation.isPending || !cancelReason.trim()}
              className="bg-red-600 hover:bg-red-700"
            >
              {cancelMutation.isPending ? (
                <>
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                  Cancelling...
                </>
              ) : (
                <>
                  <X className="h-4 w-4 mr-2" />
                  Cancel Order
                </>
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Review Consultation Prompt */}
      <AlertDialog open={reviewConsultationDialog} onOpenChange={setReviewConsultationDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle className="flex items-center space-x-2">
              <TestTube className="h-5 w-5 text-blue-500" />
              <span>Initiate Review Consultation?</span>
            </AlertDialogTitle>
            <AlertDialogDescription>
              Lab order "{cancelledOrderDetails?.serviceName}" has been cancelled. 
              Would you like to create a new review consultation for this patient? 
              This will link to the same patient and insurer, approving prior billed consultation.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel 
              onClick={() => {
                setReviewConsultationDialog(false);
                setCancelledOrderDetails(null);
              }}
              disabled={reviewConsultationMutation.isPending}
            >
              No, Skip Review
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={() => reviewConsultationMutation.mutate()}
              disabled={reviewConsultationMutation.isPending}
              className="bg-blue-600 hover:bg-blue-700"
            >
              {reviewConsultationMutation.isPending ? (
                <>
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                  Creating...
                </>
              ) : (
                "Yes, Create Review Consultation"
              )}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

client/src/components/ai-decision-panel..content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";

interface AIDecisionPanelProps {
  decision: {
    aiDecision: string;
    aiConfidence: number;
    aiReasoning: {
      reasoning?: {
        step: number;
        description: string;
        factor: string;
      }[];
      riskFactors?: string[];
      recommendations?: string[];
    };
    serviceType: string;
    estimatedCost: string;
  };
}

export function AIDecisionPanel({ decision }: AIDecisionPanelProps) {
  const getDecisionColor = (aiDecision: string) => {
    switch (aiDecision) {
      case 'approved': return 'border-green-200 bg-green-50';
      case 'denied': return 'border-red-200 bg-red-50';
      case 'review': return 'border-yellow-200 bg-yellow-50';
      default: return 'border-gray-200 bg-gray-50';
    }
  };

  const getDecisionIcon = (aiDecision: string) => {
    switch (aiDecision) {
      case 'approved': return 'fas fa-check-circle text-green-600';
      case 'denied': return 'fas fa-times-circle text-red-600';
      case 'review': return 'fas fa-exclamation-triangle text-yellow-600';
      default: return 'fas fa-question-circle text-gray-600';
    }
  };

  return (
    <Card className={getDecisionColor(decision.aiDecision)}>
      <CardHeader>
        <CardTitle className="flex items-center space-x-2">
          <i className={getDecisionIcon(decision.aiDecision)}></i>
          <span>AI Decision: {decision.aiDecision.toUpperCase()}</span>
        </CardTitle>
        <CardDescription>
          {decision.serviceType} - ${parseFloat(decision.estimatedCost).toFixed(2)}
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className="space-y-6">
          {/* Confidence Score */}
          <div>
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-medium">AI Confidence</span>
              <span className="text-sm font-bold">{decision.aiConfidence}%</span>
            </div>
            <Progress value={decision.aiConfidence} className="h-2" />
          </div>

          {/* Chain-of-Thought Reasoning */}
          {decision.aiReasoning.reasoning && decision.aiReasoning.reasoning.length > 0 && (
            <div>
              <h4 className="font-medium text-gray-800 mb-3 flex items-center space-x-2">
                <i className="fas fa-brain text-blue-500"></i>
                <span>Chain-of-Thought Reasoning</span>
              </h4>

              <div className="space-y-3">
                {decision.aiReasoning.reasoning.map((step, index) => (
                  <div key={index} className="flex items-start space-x-3">
                    <div className="w-6 h-6 bg-blue-100 rounded-full flex items-center justify-center text-xs font-bold text-blue-600">
                      {step.step}
                    </div>
                    <div className="flex-1">
                      <p className="text-sm text-gray-700">{step.description}</p>
                      <p className="text-xs text-gray-500 mt-1">Factor: {step.factor}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Risk Factors */}
          {decision.aiReasoning.riskFactors && decision.aiReasoning.riskFactors.length > 0 && (
            <div>
              <h4 className="font-medium text-gray-800 mb-3 flex items-center space-x-2">
                <i className="fas fa-exclamation-triangle text-yellow-500"></i>
                <span>Risk Factors</span>
              </h4>
              <ul className="space-y-2">
                {decision.aiReasoning.riskFactors.map((risk, index) => (
                  <li key={index} className="text-sm text-gray-700 flex items-start space-x-2">
                    <i className="fas fa-warning text-yellow-500 mt-0.5 text-xs"></i>
                    <span>{risk}</span>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Recommendations */}
          {decision.aiReasoning.recommendations && decision.aiReasoning.recommendations.length > 0 && (
            <div>
              <h4 className="font-medium text-gray-800 mb-3 flex items-center space-x-2">
                <i className="fas fa-lightbulb text-blue-500"></i>
                <span>AI Recommendations</span>
              </h4>
              <ul className="space-y-2">
                {decision.aiReasoning.recommendations.map((recommendation, index) => (
                  <li key={index} className="text-sm text-gray-700 flex items-start space-x-2">
                    <i className="fas fa-arrow-right text-blue-500 mt-0.5 text-xs"></i>
                    <span>{recommendation}</span>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex space-x-3 pt-4 border-t border-gray-200">
            {decision.aiDecision === 'approved' && (
              <Button className="flex-1 teal-button">
                Accept & Process
              </Button>
            )}
            {decision.aiDecision === 'review' && (
              <Button className="flex-1 bg-yellow-600 hover:bg-yellow-700 text-white">
                Escalate to Human Review
              </Button>
            )}
            {decision.aiDecision === 'denied' && (
              <Button className="flex-1 bg-red-600 hover:bg-red-700 text-white">
                Submit Appeal
              </Button>
            )}
            <Button variant="outline" className="flex-1">
              Generate Report
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

