    ] as PopulationTrend[],
  });

  // Fetch risk assessments
  const { data: riskAssessments, isLoading: riskLoading } = useQuery({
    queryKey: ["/api/analytics/risk-assessments"],
    queryFn: async () => [
      {
        patientId: "P98765",
        patientName: "Grace Muthoni",
        age: 45,
        gender: "Female",
        riskFactors: [
          { factor: "Hypertension", severity: "medium", impact: 6.5 },
