import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Loader2, MessageSquare, CheckCircle, XCircle, AlertTriangle } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";

interface DecisionLog {
  id: number;
  userId: number;
  patientId: number | null;
  decisionType: string;
  originalDecision: string;
  aiConfidence: string;
  reasoning: any;
  finalOutcome: string | null;
  appealOutcome: string | null;
  careManagerNotes: string | null;
  metadata: any;
  createdAt: string;
  updatedAt: string;
}

interface FeedbackFormProps {
  log: DecisionLog;
  onSuccess: () => void;
}

function FeedbackForm({ log, onSuccess }: FeedbackFormProps) {
  const [finalOutcome, setFinalOutcome] = useState(log.finalOutcome || '');
  const [appealOutcome, setAppealOutcome] = useState(log.appealOutcome || '');
  const [reviewerNotes, setReviewerNotes] = useState(log.careManagerNotes || '');

  const queryClient = useQueryClient();

  const updateFeedback = useMutation({
    mutationFn: async (data: { logId: number; finalOutcome: string; appealOutcome?: string; reviewerNotes?: string }) => {
      const response = await fetch('/api/feedback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
        credentials: 'include'
      });
      if (!response.ok) throw new Error('Failed to update feedback');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/decision-logs'] });
      onSuccess();
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!finalOutcome) return;

    updateFeedback.mutate({
      logId: log.id,
      finalOutcome,
      appealOutcome: appealOutcome || undefined,
      reviewerNotes: reviewerNotes || undefined
    });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <Label htmlFor="finalOutcome">Final Outcome *</Label>
        <Select value={finalOutcome} onValueChange={setFinalOutcome}>
          <SelectTrigger>
            <SelectValue placeholder="Select final outcome" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="approved">Approved</SelectItem>
            <SelectItem value="denied">Denied</SelectItem>
            <SelectItem value="approved_with_conditions">Approved with Conditions</SelectItem>
            <SelectItem value="pending_review">Pending Review</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <div>
        <Label htmlFor="appealOutcome">Appeal Outcome (if applicable)</Label>
        <Select value={appealOutcome} onValueChange={setAppealOutcome}>
          <SelectTrigger>
            <SelectValue placeholder="Select appeal outcome" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="">No Appeal</SelectItem>
            <SelectItem value="appeal_approved">Appeal Approved</SelectItem>
            <SelectItem value="appeal_denied">Appeal Denied</SelectItem>
            <SelectItem value="appeal_pending">Appeal Pending</SelectItem>
          </SelectContent>
        </Select>
      </div>

      <div>
        <Label htmlFor="reviewerNotes">Reviewer Notes</Label>
        <Textarea
          id="reviewerNotes"
          value={reviewerNotes}
          onChange={(e) => setReviewerNotes(e.target.value)}
          placeholder="Add any notes about the decision or review process..."
          rows={3}
        />
      </div>

      <Button 
        type="submit" 
        disabled={!finalOutcome || updateFeedback.isPending}
        className="w-full bg-teal-600 hover:bg-teal-700"
      >
        {updateFeedback.isPending ? (
          <>
            <Loader2 className="w-4 h-4 mr-2 animate-spin" />
            Updating...
          </>
        ) : (
          <>
            <CheckCircle className="w-4 h-4 mr-2" />
            Update Feedback
          </>
        )}
      </Button>
    </form>
  );
}

export function DecisionFeedbackPanel() {
  const [selectedLog, setSelectedLog] = useState<DecisionLog | null>(null);
  const [filterType, setFilterType] = useState<string>('');

  const { data: decisionLogs = [], isLoading } = useQuery({
    queryKey: ['/api/decision-logs', filterType],
    queryFn: async () => {
      const url = filterType ? `/api/decision-logs?type=${filterType}` : '/api/decision-logs';
      const response = await fetch(url, { credentials: 'include' });
      if (!response.ok) throw new Error('Failed to fetch decision logs');
      return response.json() as Promise<DecisionLog[]>;
    }
  });

  const getDecisionIcon = (decision: string) => {
    switch (decision.toLowerCase()) {
      case 'approved':
        return <CheckCircle className="w-4 h-4 text-green-600" />;
      case 'denied':
        return <XCircle className="w-4 h-4 text-red-600" />;
      default:
        return <AlertTriangle className="w-4 h-4 text-yellow-600" />;
    }
  };

  const getOutcomeColor = (outcome: string | null) => {
    if (!outcome) return 'bg-gray-100 text-gray-800';
    switch (outcome.toLowerCase()) {
      case 'approved':
      case 'appeal_approved':
        return 'bg-green-100 text-green-800';
      case 'denied':
      case 'appeal_denied':
        return 'bg-red-100 text-red-800';
      case 'approved_with_conditions':
        return 'bg-blue-100 text-blue-800';
      default:
        return 'bg-yellow-100 text-yellow-800';
    }
  };

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
      {/* Decision Logs List */}
      <div className="lg:col-span-2">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <MessageSquare className="w-5 h-5 text-blue-600" />
              Decision Logs
            </CardTitle>
            <CardDescription>
              Track and provide feedback on AI decisions
            </CardDescription>
            <div className="flex gap-2">
              <Select value={filterType} onValueChange={setFilterType}>
                <SelectTrigger className="w-48">
                  <SelectValue placeholder="Filter by type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">All Types</SelectItem>
                  <SelectItem value="preauth">Preauthorization</SelectItem>
                  <SelectItem value="pharmacy_validation">Pharmacy Validation</SelectItem>
                  <SelectItem value="claims_validation">Claims Validation</SelectItem>
                  <SelectItem value="fraud_detection">Fraud Detection</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="flex items-center justify-center py-8">
                <Loader2 className="w-6 h-6 animate-spin text-teal-600" />
                <span className="ml-2">Loading decision logs...</span>
              </div>
            ) : (
              <div className="space-y-3">
                {decisionLogs.map((log) => (
                  <div
                    key={log.id}
                    className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                      selectedLog?.id === log.id 
                        ? 'border-teal-500 bg-teal-50' 
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                    onClick={() => setSelectedLog(log)}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center gap-2">
                        {getDecisionIcon(log.originalDecision)}
                        <span className="font-medium">
                          {log.decisionType.replace('_', ' ').toUpperCase()}
                        </span>
                        <Badge variant="outline">
                          {log.aiConfidence}% confidence
                        </Badge>
                      </div>
                      <div className="text-sm text-gray-500">
                        {new Date(log.createdAt).toLocaleDateString()}
                      </div>
                    </div>

                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <span className="text-gray-600">Original:</span>
                        <Badge className={`ml-1 ${getOutcomeColor(log.originalDecision)}`}>
                          {log.originalDecision}
                        </Badge>
                      </div>
                      <div>
                        <span className="text-gray-600">Final:</span>
                        {log.finalOutcome ? (
                          <Badge className={`ml-1 ${getOutcomeColor(log.finalOutcome)}`}>
                            {log.finalOutcome}
                          </Badge>
                        ) : (
                          <span className="ml-1 text-gray-400">Pending</span>
                        )}
                      </div>
                    </div>

                    {log.metadata && (
                      <div className="mt-2 text-xs text-gray-600">
                        {log.metadata.serviceType && (
                          <span>Service: {log.metadata.serviceType} ‚Ä¢ </span>
                        )}
                        {log.metadata.estimatedCost && (
                          <span>Cost: KES {log.metadata.estimatedCost}</span>
                        )}
                      </div>
                    )}
                  </div>
                ))}

                {decisionLogs.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    No decision logs found for the selected criteria
                  </div>
                )}
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Feedback Form */}
      <div>
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <CheckCircle className="w-5 h-5 text-green-600" />
              Provide Feedback
            </CardTitle>
            <CardDescription>
              Update outcome and add reviewer notes
            </CardDescription>
          </CardHeader>
          <CardContent>
            {selectedLog ? (
              <div className="space-y-4">
                {/* Log Details */}
                <div className="p-3 bg-gray-50 rounded-lg space-y-2">
                  <div className="flex items-center gap-2">
                    {getDecisionIcon(selectedLog.originalDecision)}
                    <span className="font-medium">
                      {selectedLog.decisionType.replace('_', ' ').toUpperCase()}
                    </span>
                  </div>

                  <div className="text-sm space-y-1">
                    <div>
                      <span className="text-gray-600">Original Decision:</span>
                      <Badge className={`ml-1 ${getOutcomeColor(selectedLog.originalDecision)}`}>
                        {selectedLog.originalDecision}
                      </Badge>
                    </div>
                    <div>
                      <span className="text-gray-600">Confidence:</span>
                      <span className="ml-1 font-medium">{selectedLog.aiConfidence}%</span>
                    </div>
                    <div>
                      <span className="text-gray-600">Date:</span>
                      <span className="ml-1">{new Date(selectedLog.createdAt).toLocaleString()}</span>
                    </div>
                  </div>

                  {selectedLog.reasoning && Array.isArray(selectedLog.reasoning) && (
                    <div className="mt-2">
                      <span className="text-xs text-gray-600 font-medium">AI Reasoning:</span>
                      <div className="text-xs mt-1 space-y-1">
                        {selectedLog.reasoning.slice(0, 3).map((reason: string, index: number) => (
                          <div key={index} className="text-gray-700">
                            ‚Ä¢ {reason}
                          </div>
                        ))}
                      </div>
                    </div>
                  )}
                </div>

                {/* Feedback Form */}
                <FeedbackForm 
                  log={selectedLog} 
                  onSuccess={() => setSelectedLog(null)} 
                />
              </div>
            ) : (
              <div className="text-center py-8 text-gray-500">
                <MessageSquare className="w-12 h-12 mx-auto mb-3 text-gray-300" />
                <p>Select a decision log to provide feedback</p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/enhanced-claim-tra.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  FileText, 
  Clock, 
  CheckCircle, 
  XCircle, 
  AlertTriangle,
  Pill,
  TestTube,
  Stethoscope,
  Calendar,
  DollarSign,
  User,
  Building2
} from "lucide-react";
import { useQuery } from "@tanstack/react-query";

interface ClaimService {
  id: string;
  serviceName: string;
  serviceType: 'consultation' | 'lab' | 'pharmacy' | 'procedure';
  status: 'pending' | 'fulfilled' | 'cancelled' | 'expired';
  prescribedBy: string;
  startDate: string;
  durationDays?: number;
  quantity: number;
  unitCost: number;
  totalCost: number;
  notes?: string;
}

interface EnhancedClaim {
  id: string;
  patientId: string;
  patientName: string;
  insurerName: string;
  schemeName: string;
  claimNumber: string;
  totalAmount: number;
  status: 'pending' | 'processing' | 'approved' | 'denied' | 'voided';
  isActive: boolean;
  submittedAt: string;
  services: ClaimService[];
}

interface ClaimTrackerProps {
  patientId?: string;
  showAllClaims?: boolean;
}

export function EnhancedClaimTracker({ patientId, showAllClaims = false }: ClaimTrackerProps) {
  const [selectedClaim, setSelectedClaim] = useState<EnhancedClaim | null>(null);

  const { data: claims = [], isLoading } = useQuery({
    queryKey: ['/api/enhanced-claims', patientId],
    queryFn: async () => {
      const url = patientId 
        ? `/api/enhanced-claims/patient/${patientId}` 
        : '/api/enhanced-claims';
      const response = await fetch(url, { credentials: 'include' });
      if (!response.ok) throw new Error('Failed to fetch claims');
      return response.json() as Promise<EnhancedClaim[]>;
    }
  });

  const getServiceIcon = (serviceType: string) => {
    switch (serviceType) {
      case 'consultation':
        return <Stethoscope className="w-4 h-4" />;
      case 'pharmacy':
        return <Pill className="w-4 h-4" />;
      case 'lab':
        return <TestTube className="w-4 h-4" />;
      case 'procedure':
        return <FileText className="w-4 h-4" />;
      default:
        return <FileText className="w-4 h-4" />;
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'fulfilled':
        return <CheckCircle className="w-4 h-4 text-green-600" />;
      case 'cancelled':
      case 'expired':
        return <XCircle className="w-4 h-4 text-red-600" />;
      case 'pending':
        return <Clock className="w-4 h-4 text-yellow-600" />;
      default:
        return <AlertTriangle className="w-4 h-4 text-gray-600" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'fulfilled':
      case 'approved':
        return 'bg-green-100 text-green-800';
      case 'cancelled':
      case 'denied':
      case 'expired':
        return 'bg-red-100 text-red-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'processing':
        return 'bg-blue-100 text-blue-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const calculateClaimProgress = (claim: EnhancedClaim) => {
    const totalServices = claim.services.length;
    const completedServices = claim.services.filter(s => s.status === 'fulfilled').length;
    return totalServices > 0 ? (completedServices / totalServices) * 100 : 0;
  };

  const getActiveClaims = () => claims.filter(claim => claim.isActive);
  const getCompletedClaims = () => claims.filter(claim => !claim.isActive);

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="flex items-center justify-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-teal-600"></div>
            <span className="ml-2">Loading claims...</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Claims Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <FileText className="w-5 h-5 text-blue-600" />
              <div>
                <p className="text-sm text-gray-600">Active Claims</p>
                <p className="text-2xl font-bold">{getActiveClaims().length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <DollarSign className="w-5 h-5 text-green-600" />
              <div>
                <p className="text-sm text-gray-600">Total Value</p>
                <p className="text-2xl font-bold">
                  KES {claims.reduce((sum, claim) => sum + claim.totalAmount, 0).toLocaleString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <CheckCircle className="w-5 h-5 text-teal-600" />
              <div>
                <p className="text-sm text-gray-600">Completed</p>
                <p className="text-2xl font-bold">{getCompletedClaims().length}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Claims Tabs */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="w-5 h-5 text-blue-600" />
            Claims Management
          </CardTitle>
          <CardDescription>
            Track and manage patient claims with detailed service breakdown
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Tabs defaultValue="active" className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="active">Active Claims ({getActiveClaims().length})</TabsTrigger>
              <TabsTrigger value="completed">Completed ({getCompletedClaims().length})</TabsTrigger>
            </TabsList>

            <TabsContent value="active" className="mt-4">
              <div className="space-y-4">
                {getActiveClaims().map((claim) => (
                  <ClaimCard 
                    key={claim.id} 
                    claim={claim} 
                    onSelect={setSelectedClaim}
                    calculateProgress={calculateClaimProgress}
                    getStatusColor={getStatusColor}
                  />
                ))}
                {getActiveClaims().length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    <FileText className="w-12 h-12 mx-auto mb-3 text-gray-300" />
                    <p>No active claims found</p>
                  </div>
                )}
              </div>
            </TabsContent>

            <TabsContent value="completed" className="mt-4">
              <div className="space-y-4">
                {getCompletedClaims().map((claim) => (
                  <ClaimCard 
                    key={claim.id} 
                    claim={claim} 
                    onSelect={setSelectedClaim}
                    calculateProgress={calculateClaimProgress}
                    getStatusColor={getStatusColor}
                  />
                ))}
                {getCompletedClaims().length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    <CheckCircle className="w-12 h-12 mx-auto mb-3 text-gray-300" />
                    <p>No completed claims found</p>
                  </div>
                )}
              </div>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>

      {/* Detailed Claim View */}
      {selectedClaim && (
        <ClaimDetailsModal 
          claim={selectedClaim} 
          onClose={() => setSelectedClaim(null)}
          getServiceIcon={getServiceIcon}
          getStatusIcon={getStatusIcon}
          getStatusColor={getStatusColor}
        />
      )}
    </div>
  );
}

function ClaimCard({ 
  claim, 
  onSelect, 
  calculateProgress, 
  getStatusColor 
}: {
  claim: EnhancedClaim;
  onSelect: (claim: EnhancedClaim) => void;
  calculateProgress: (claim: EnhancedClaim) => number;
  getStatusColor: (status: string) => string;
}) {
  const progress = calculateProgress(claim);

  return (
    <Card 
      className="cursor-pointer hover:shadow-md transition-shadow border-l-4 border-l-teal-500"
      onClick={() => onSelect(claim)}
    >
      <CardContent className="p-4">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center gap-3">
            <div>
              <h3 className="font-semibold">{claim.claimNumber}</h3>
              <p className="text-sm text-gray-600">
                {claim.patientName} ‚Ä¢ {claim.insurerName}
              </p>
            </div>
          </div>
          <div className="text-right">
            <Badge className={getStatusColor(claim.status)}>
              {claim.status.toUpperCase()}
            </Badge>
            <p className="text-sm font-medium mt-1">
              KES {claim.totalAmount.toLocaleString()}
            </p>
          </div>
        </div>

        <div className="space-y-2">
          <div className="flex items-center justify-between text-sm">
            <span>{claim.services.length} services</span>
            <span>{Math.round(progress)}% complete</span>
          </div>
          <Progress value={progress} className="h-2" />
        </div>

        <div className="flex items-center justify-between mt-3 text-xs text-gray-500">
          <span>Submitted: {new Date(claim.submittedAt).toLocaleDateString()}</span>
          <span>Scheme: {claim.schemeName}</span>
        </div>
      </CardContent>
    </Card>
  );
}

function ClaimDetailsModal({ 
  claim, 
  onClose, 
  getServiceIcon, 
  getStatusIcon, 
  getStatusColor 
}: {
  claim: EnhancedClaim;
  onClose: () => void;
  getServiceIcon: (type: string) => React.ReactNode;
  getStatusIcon: (status: string) => React.ReactNode;
  getStatusColor: (status: string) => string;
}) {
  return (
    <Card>
      <CardHeader className="border-b">
        <div className="flex items-center justify-between">
          <div>
            <CardTitle>{claim.claimNumber}</CardTitle>
            <CardDescription>
              Detailed claim information and service breakdown
            </CardDescription>
          </div>
          <Button variant="outline" onClick={onClose}>
            Close
          </Button>
        </div>
      </CardHeader>
      <CardContent className="p-6">
        {/* Claim Summary */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="flex items-center gap-2">
            <User className="w-4 h-4 text-gray-600" />
            <div>
              <p className="text-sm text-gray-600">Patient</p>
              <p className="font-medium">{claim.patientName}</p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <Building2 className="w-4 h-4 text-gray-600" />
            <div>
              <p className="text-sm text-gray-600">Insurer</p>
              <p className="font-medium">{claim.insurerName}</p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            <Calendar className="w-4 h-4 text-gray-600" />
            <div>
              <p className="text-sm text-gray-600">Submitted</p>
              <p className="font-medium">{new Date(claim.submittedAt).toLocaleDateString()}</p>
            </div>
          </div>
        </div>

        {/* Services Breakdown */}
        <div>
          <h4 className="font-semibold mb-4">Services ({claim.services.length})</h4>
          <div className="space-y-3">
            {claim.services.map((service) => (
              <div 
                key={service.id} 
                className="flex items-center justify-between p-3 border rounded-lg"
              >
                <div className="flex items-center gap-3">
                  {getServiceIcon(service.serviceType)}
                  <div>
                    <p className="font-medium">{service.serviceName}</p>
                    <p className="text-sm text-gray-600">
                      Qty: {service.quantity} ‚Ä¢ Prescribed by: {service.prescribedBy}
                    </p>
                    {service.durationDays && (
                      <p className="text-xs text-gray-500">
                        Duration: {service.durationDays} days
                      </p>
                    )}
                  </div>
                </div>
                <div className="text-right">
                  <div className="flex items-center gap-2 mb-1">
                    {getStatusIcon(service.status)}
                    <Badge className={getStatusColor(service.status)}>
                      {service.status.toUpperCase()}
                    </Badge>
                  </div>
                  <p className="font-medium">
                    KES {service.totalCost.toLocaleString()}
                  </p>
                  <p className="text-xs text-gray-500">
                    @{service.unitCost} each
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/erlessed-logo.tsx.content:
import { cn } from "@/lib/utils";

interface ErllessedLogoProps {
  className?: string;
  size?: "sm" | "md" | "lg";
}

export function ErllessedLogo({ className, size = "md" }: ErllessedLogoProps) {
  const sizeClasses = {
    sm: "w-6 h-6",
    md: "w-10 h-10",
    lg: "w-16 h-16"
  };

  const textSizeClasses = {
    sm: "text-lg",
    md: "text-xl",
    lg: "text-3xl"
  };

  return (
    <div className={cn("flex items-center space-x-3", className)}>
      {/* Medical Cross Logo */}
      <div className="relative">
        <div className={cn(
          "bg-teal-primary rounded-lg flex items-center justify-center",
          sizeClasses[size]
        )}>
          <div className={cn(
            "relative",
            size === "sm" ? "w-4 h-4" : 
            size === "md" ? "w-6 h-6" : "w-8 h-8"
          )}>
            {/* Horizontal bar */}
            <div className={cn(
              "absolute bg-teal-secondary rounded-sm",
              size === "sm" ? "inset-x-1 inset-y-2 h-1" :
              size === "md" ? "inset-x-1.5 inset-y-2.5 h-1" : "inset-x-2 inset-y-3 h-2"
            )}></div>
            {/* Vertical bar */}
            <div className={cn(
              "absolute bg-teal-secondary rounded-sm",
              size === "sm" ? "inset-y-1 inset-x-2 w-1" :
              size === "md" ? "inset-y-1.5 inset-x-2.5 w-1" : "inset-y-2 inset-x-3 w-2"
            )}></div>
            {/* Center circle */}
            <div className={cn(
              "absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-white rounded-full",
              size === "sm" ? "w-1.5 h-1.5" :
              size === "md" ? "w-2 h-2" : "w-3 h-3"
            )}></div>
          </div>
        </div>
      </div>

      {/* Text */}
      <div>
        <h1 className={cn("font-bold text-teal-primary", textSizeClasses[size])}>
          Erlessed
        </h1>
        <p className={cn(
          "text-gray-500",
          size === "sm" ? "text-xs" : "text-xs"
        )}>
          powered by <span className="font-semibold text-black">Aboolean</span>
        </p>
      </div>
    </div>
  );
}

client/src/components/layout.tsx.content:
import { ReactNode } from "react";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { 
  DropdownMenu, DropdownMenuContent, DropdownMenuItem, 
  DropdownMenuLabel, DropdownMenuSeparator, DropdownMenuTrigger 
} from "@/components/ui/dropdown-menu";
import { 
  Sidebar, SidebarContent, SidebarFooter, SidebarGroup, 
  SidebarGroupContent, SidebarMenu, SidebarMenuButton, 
  SidebarMenuItem, SidebarProvider, SidebarTrigger 
} from "@/components/ui/sidebar";
import { 
  Home, Users, FileText, Brain, Pill, BarChart3, 
  Link2, DollarSign, Settings, Bell, LogOut 
} from "lucide-react";
import { Link, useLocation } from "wouter";
import { Logo } from "@/components/logo";

interface LayoutProps {
  children: ReactNode;
}

const navigationItems = [
  { title: "Dashboard", url: "/", icon: Home, roles: ["all"] },
  { title: "Patient Verification", url: "/verification", icon: Users, roles: ["front-office", "doctor", "care-manager"] },
  { title: "Claims Processing", url: "/claims", icon: FileText, roles: ["front-office", "doctor", "care-manager"] },
  { title: "AI Preauthorization", url: "/preauth", icon: Brain, roles: ["doctor", "care-manager"] },
  { title: "Pharmacy", url: "/pharmacy", icon: Pill, roles: ["pharmacy", "care-manager"] },
  { title: "Analytics", url: "/analytics", icon: BarChart3, roles: ["care-manager", "debtors"] },
  { title: "Blockchain", url: "/blockchain", icon: Link2, roles: ["care-manager"] },
  { title: "Debtors", url: "/debtors", icon: DollarSign, roles: ["debtors", "care-manager"] },
];

export function Layout({ children }: LayoutProps) {
  const { user, logoutMutation } = useAuth();
  const [location] = useLocation();

  const getFilteredNavigation = () => {
    if (!user) return [];
    return navigationItems.filter(item => 
      item.roles.includes("all") || item.roles.includes(user.role)
    );
  };

  const getRoleDisplayName = (role: string) => {
    const roleNames = {
      'front-office': 'Front Office',
      'doctor': 'Physician',
      'lab': 'Laboratory',
      'pharmacy': 'Pharmacy',
      'debtors': 'Debtors',
      'care-manager': 'Care Manager'
    };
    return roleNames[role as keyof typeof roleNames] || role;
  };

  const getUserInitials = (username: string) => {
    return username.slice(0, 2).toUpperCase();
  };

  const handleLogout = () => {
    logoutMutation.mutate();
  };

  return (
    <SidebarProvider>
      <div className="min-h-screen flex w-full">
        <Sidebar className="border-r border-gray-200">
          <SidebarContent>
            {/* Logo Section */}
            <div className="p-6 border-b border-gray-200">
              <Link href="/">
                <div className="flex items-center space-x-3">
                  <Logo />
                  <div>
                    <h1 className="text-xl font-bold text-teal-600">Erlessed</h1>
                    <p className="text-xs text-gray-500">powered by <span className="font-semibold text-black">Aboolean</span></p>
                  </div>
                </div>
              </Link>
            </div>

            {/* Navigation */}
            <SidebarGroup>
              <SidebarGroupContent>
                <SidebarMenu>
                  {getFilteredNavigation().map((item) => (
                    <SidebarMenuItem key={item.title}>
                      <SidebarMenuButton 
                        asChild 
                        isActive={location === item.url}
                        className={location === item.url ? "bg-teal-50 text-teal-700" : ""}
                      >
                        <Link href={item.url}>
                          <item.icon className="w-4 h-4" />
                          <span>{item.title}</span>
                        </Link>
                      </SidebarMenuButton>
                    </SidebarMenuItem>
                  ))}
                </SidebarMenu>
              </SidebarGroupContent>
            </SidebarGroup>
          </SidebarContent>

          {/* User Profile Footer */}
          <SidebarFooter className="border-t border-gray-200 p-4">
            {user && (
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <Button variant="ghost" className="w-full justify-start space-x-3 h-auto p-3">
                    <Avatar className="h-8 w-8">
                      <AvatarFallback className="bg-teal-100 text-teal-700">
                        {getUserInitials(user.username)}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1 text-left">
                      <p className="text-sm font-medium">{user.username}</p>
                      <p className="text-xs text-gray-500">{getRoleDisplayName(user.role)}</p>
                    </div>
                  </Button>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end" className="w-56">
                  <DropdownMenuLabel>My Account</DropdownMenuLabel>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem>
                    <Settings className="mr-2 h-4 w-4" />
                    <span>Settings</span>
                  </DropdownMenuItem>
                  <DropdownMenuSeparator />
                  <DropdownMenuItem onClick={handleLogout}>
                    <LogOut className="mr-2 h-4 w-4" />
                    <span>Log out</span>
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            )}
          </SidebarFooter>
        </Sidebar>

        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          {/* Top Navigation Bar */}
          <header className="bg-white border-b border-gray-200 px-6 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <SidebarTrigger />
                {user && (
                  <Badge variant="outline" className="text-teal-700 border-teal-200">
                    {getRoleDisplayName(user.role)}
                  </Badge>
                )}
              </div>

              <div className="flex items-center space-x-4">
                {/* AI Status Indicator */}
                <div className="flex items-center space-x-2 bg-teal-50 px-3 py-1 rounded-full">
                  <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                  <span className="text-sm text-teal-700 font-medium">AI Active</span>
                </div>

                {/* Notifications */}
                <Button variant="ghost" size="icon" className="relative">
                  <Bell className="h-4 w-4" />
                  <span className="absolute -top-1 -right-1 w-5 h-5 bg-red-500 text-white text-xs rounded-full flex items-center justify-center">
                    3
                  </span>
                </Button>
              </div>
            </div>
          </header>

          {/* Page Content */}
          <main className="flex-1 bg-gray-50">
            {children}
          </main>
        </div>
      </div>
    </SidebarProvider>
  );
}

