client/src/components/mobile-nav.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Link, useLocation } from "wouter";
import { 
  Menu, X, User, MapPin, Heart, FileText, 
  Calendar, Pill, CreditCard, Phone, Settings,
  LogOut, Home, Shield
} from "lucide-react";

interface MobileNavProps {
  userRole?: string;
}

interface NavLink {
  path: string;
  icon: React.ComponentType<any>;
  label: string;
  badge?: string;
}

export function MobileNav({ userRole }: MobileNavProps) {
  const [isOpen, setIsOpen] = useState(false);
  const { user, logout } = useAuth();
  const [location] = useLocation();

  const fieldWorkerLinks: NavLink[] = [
    { path: "/mobile-field-worker", icon: MapPin, label: "Field Visits", badge: "3" },
    { path: "/patient-verification", icon: Shield, label: "Verify Patient" },
    { path: "/emergency-contacts", icon: Phone, label: "Emergency" },
  ];

  const patientPortalLinks: NavLink[] = [
    { path: "/mobile-patient-portal", icon: Home, label: "Dashboard" },
    { path: "/appointments", icon: Calendar, label: "Appointments" },
    { path: "/prescriptions", icon: Pill, label: "Medications" },
    { path: "/claims", icon: CreditCard, label: "Claims" },
    { path: "/health-records", icon: Heart, label: "Health Data" },
  ];

  const getNavLinks = () => {
    switch (userRole) {
      case "field-worker":
        return fieldWorkerLinks;
      case "patient":
        return patientPortalLinks;
      default:
        return [...fieldWorkerLinks, ...patientPortalLinks];
    }
  };

  const isActive = (path: string) => {
    return location === path || location.startsWith(path);
  };

  return (
    <>
      {/* Mobile Header */}
      <div className="lg:hidden bg-gradient-to-r from-teal-600 to-blue-600 text-white p-4 shadow-lg">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setIsOpen(!isOpen)}
              className="text-white hover:bg-white/20"
            >
              {isOpen ? <X className="w-5 h-5" /> : <Menu className="w-5 h-5" />}
            </Button>
            <div>
              <h1 className="text-lg font-bold">Erlessed Mobile</h1>
              <p className="text-sm opacity-90">{userRole || "Healthcare"}</p>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <div className="w-8 h-8 bg-white/20 rounded-full flex items-center justify-center">
              <User className="w-4 h-4" />
            </div>
          </div>
        </div>
      </div>

      {/* Mobile Side Menu */}
      {isOpen && (
        <div className="lg:hidden fixed inset-0 z-50 bg-black/50" onClick={() => setIsOpen(false)}>
          <div className="w-80 h-full bg-white shadow-xl" onClick={e => e.stopPropagation()}>
            {/* Menu Header */}
            <div className="bg-gradient-to-r from-teal-600 to-blue-600 text-white p-4">
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="w-10 h-10 bg-white/20 rounded-full flex items-center justify-center">
                    <User className="w-5 h-5" />
                  </div>
                  <div>
                    <p className="font-semibold">{user?.username}</p>
                    <p className="text-sm opacity-90 capitalize">{userRole?.replace('-', ' ')}</p>
                  </div>
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setIsOpen(false)}
                  className="text-white hover:bg-white/20"
                >
                  <X className="w-4 h-4" />
                </Button>
              </div>
            </div>

            {/* Navigation Links */}
            <div className="p-4 space-y-2">
              {getNavLinks().map((link) => (
                <Link key={link.path} href={link.path}>
                  <div
                    className={`flex items-center space-x-3 p-3 rounded-lg transition-colors ${
                      isActive(link.path)
                        ? "bg-teal-50 text-teal-700 border border-teal-200"
                        : "hover:bg-gray-50"
                    }`}
                    onClick={() => setIsOpen(false)}
                  >
                    <link.icon className="w-5 h-5" />
                    <span className="flex-1 font-medium">{link.label}</span>
                    {link.badge && (
                      <Badge variant="secondary" className="bg-teal-100 text-teal-700">
                        {link.badge}
                      </Badge>
                    )}
                  </div>
                </Link>
              ))}
            </div>

            {/* Quick Actions */}
            <div className="p-4 border-t">
              <h3 className="text-sm font-semibold text-gray-700 mb-3">Quick Actions</h3>
              <div className="space-y-2">
                <Button className="w-full justify-start bg-red-600 hover:bg-red-700">
                  <Phone className="w-4 h-4 mr-2" />
                  Emergency Call
                </Button>
                <Button variant="outline" className="w-full justify-start">
                  <Settings className="w-4 h-4 mr-2" />
                  Settings
                </Button>
              </div>
            </div>

            {/* Logout */}
            <div className="absolute bottom-0 left-0 right-0 p-4 border-t bg-gray-50">
              <Button
                variant="outline"
                className="w-full justify-start text-red-600 border-red-200 hover:bg-red-50"
                onClick={() => {
                  logout();
                  setIsOpen(false);
                }}
              >
                <LogOut className="w-4 h-4 mr-2" />
                Sign Out
              </Button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

// Mobile Quick Access Widget
export function MobileQuickAccess() {
  return (
    <div className="lg:hidden fixed bottom-4 right-4 z-40">
      <Card className="shadow-lg">
        <CardContent className="p-3">
          <div className="flex space-x-2">
            <Button size="sm" className="bg-red-600 hover:bg-red-700">
              <Phone className="w-4 h-4" />
            </Button>
            <Button size="sm" variant="outline">
              <FileText className="w-4 h-4" />
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/registration-valid.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Shield, 
  CheckCircle, 
  XCircle, 
  AlertTriangle, 
  Search, 
  Users, 
  Building2,
  Calendar,
  FileText
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface VerificationResult {
  isValid: boolean;
  practitioner?: {
    fullName: string;
    status: string;
    cadre: string;
    specialization: string;
    facility: string;
    board: string;
    licenseExpiryDate: string;
    practiceLicense: string;
  };
  error?: string;
}

interface RegistrationStats {
  totalPractitioners: number;
  activePractitioners: number;
  suspendedPractitioners: number;
  expiredLicenses: number;
  boardBreakdown: Record<string, number>;
}

export function RegistrationValidator() {
  const { toast } = useToast();
  const [registrationNumber, setRegistrationNumber] = useState("");
  const [selectedCadre, setSelectedCadre] = useState("");
  const [verificationResult, setVerificationResult] = useState<VerificationResult | null>(null);
  const [searchQuery, setSearchQuery] = useState({
    name: "",
    facility: "",
    cadre: "",
    board: ""
  });

  // Fetch registration boards
  const { data: boards = [] } = useQuery({
    queryKey: ["/api/registration/boards"],
    queryFn: async () => {
      const response = await fetch("/api/registration/boards");
      const data = await response.json();
      return data.boards || [];
    },
  });

  // Fetch registration statistics
  const { data: stats } = useQuery({
    queryKey: ["/api/registration/statistics"],
    queryFn: async () => {
      const response = await fetch("/api/registration/statistics");
      const data = await response.json();
      return data as RegistrationStats;
    },
  });

  // Verification mutation
  const verifyMutation = useMutation({
    mutationFn: async (data: { registrationNumber: string; cadre?: string }) => {
      const response = await fetch("/api/verify-registration", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || "Verification failed");
      }

      return result as VerificationResult;
    },
    onSuccess: (result) => {
      setVerificationResult(result);
      toast({
        title: "Verification Complete",
        description: result.isValid ? "Registration verified successfully" : "Registration verification failed",
        variant: result.isValid ? "default" : "destructive",
      });
    },
    onError: (error: Error) => {
      setVerificationResult({
        isValid: false,
        error: error.message
      });
      toast({
        title: "Verification Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Search mutation
  const searchMutation = useMutation({
    mutationFn: async (query: typeof searchQuery) => {
      const params = new URLSearchParams();
      Object.entries(query).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });

      const response = await fetch(`/api/registration/search?${params}`, {
        credentials: 'include'
      });
      const data = await response.json();
      return data.practitioners || [];
    },
    onSuccess: () => {
      toast({
        title: "Search Complete",
        description: "Practitioner search completed successfully",
      });
    },
  });

  const handleVerify = () => {
    if (!registrationNumber.trim()) {
      toast({
        title: "Error",
        description: "Please enter a registration number",
        variant: "destructive",
      });
      return;
    }

    verifyMutation.mutate({
      registrationNumber: registrationNumber.trim(),
      cadre: selectedCadre || undefined
    });
  };

  const handleSearch = () => {
    searchMutation.mutate(searchQuery);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "active":
        return <Badge className="bg-green-100 text-green-800"><CheckCircle className="w-3 h-3 mr-1" />Active</Badge>;
      case "suspended":
        return <Badge className="bg-red-100 text-red-800"><XCircle className="w-3 h-3 mr-1" />Suspended</Badge>;
      case "inactive":
        return <Badge className="bg-gray-100 text-gray-800"><AlertTriangle className="w-3 h-3 mr-1" />Inactive</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const sampleRegistrations = [
    { number: "KMPDC/001/2018", cadre: "Medical Doctor", description: "Active doctor registration" },
    { number: "PPB/001/2018", cadre: "Pharmacist", description: "Active pharmacist registration" },
    { number: "COC/001/2019", cadre: "Clinical Officer", description: "Active clinical officer" },
    { number: "KMPDC/004/2017", cadre: "Medical Doctor", description: "Suspended registration" },
    { number: "COC/003/2020", cadre: "Clinical Officer", description: "Expired license" },
  ];

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Shield className="w-5 h-5 mr-2 text-teal-600" />
            Kenya Clinical Registration Validator
          </CardTitle>
          <CardDescription>
            Verify professional licenses with Kenya's clinical registration boards (KMPDC, COC, PPB)
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Tabs defaultValue="verify" className="space-y-6">
            <TabsList className="grid w-full grid-cols-3">
              <TabsTrigger value="verify">Verify Registration</TabsTrigger>
              <TabsTrigger value="search">Search Practitioners</TabsTrigger>
              <TabsTrigger value="stats">Statistics</TabsTrigger>
            </TabsList>

            <TabsContent value="verify" className="space-y-6">
              {/* Sample Registrations */}
              <div>
                <h3 className="font-semibold mb-3">Sample Registrations for Testing</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                  {sampleRegistrations.map((sample, index) => (
                    <Card key={index} className="cursor-pointer hover:shadow-md transition-shadow"
                          onClick={() => {
                            setRegistrationNumber(sample.number);
                            setSelectedCadre(sample.cadre);
                          }}>
                      <CardContent className="p-3">
                        <div className="flex justify-between items-start">
                          <div>
                            <p className="font-mono text-sm font-medium">{sample.number}</p>
                            <p className="text-xs text-gray-600">{sample.cadre}</p>
                          </div>
                          <Badge variant="outline" className="text-xs">
                            {sample.description}
                          </Badge>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </div>

              <Separator />

              {/* Verification Form */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="regNumber">Registration Number</Label>
                  <Input
                    id="regNumber"
                    value={registrationNumber}
                    onChange={(e) => setRegistrationNumber(e.target.value)}
                    placeholder="e.g., KMPDC/001/2018"
                    className="font-mono"
                  />
                </div>
                <div>
                  <Label htmlFor="cadre">Expected Cadre (Optional)</Label>
                  <Select value={selectedCadre} onValueChange={setSelectedCadre}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select cadre" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="">Any Cadre</SelectItem>
                      <SelectItem value="Medical Doctor">Medical Doctor</SelectItem>
                      <SelectItem value="Pharmacist">Pharmacist</SelectItem>
                      <SelectItem value="Clinical Officer">Clinical Officer</SelectItem>
                      <SelectItem value="Nurse">Nurse</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <Button 
                onClick={handleVerify}
                disabled={verifyMutation.isPending}
                className="w-full bg-teal-600 hover:bg-teal-700"
              >
                {verifyMutation.isPending ? "Verifying..." : "Verify Registration"}
                <Shield className="w-4 h-4 ml-2" />
              </Button>

              {/* Verification Results */}
              {verificationResult && (
                <Alert className={verificationResult.isValid ? "border-green-200 bg-green-50" : "border-red-200 bg-red-50"}>
                  <div className="flex items-start">
                    {verificationResult.isValid ? (
                      <CheckCircle className="w-5 h-5 text-green-600 mr-3 mt-0.5" />
                    ) : (
                      <XCircle className="w-5 h-5 text-red-600 mr-3 mt-0.5" />
                    )}
                    <div className="flex-1">
                      <AlertDescription>
                        {verificationResult.isValid ? (
                          <div className="space-y-3">
                            <p className="font-semibold text-green-800">Registration Verified Successfully</p>
                            <div className="bg-white rounded-lg p-4 space-y-2">
                              <div className="grid grid-cols-2 gap-4">
                                <div>
                                  <p className="text-sm font-medium text-gray-700">Full Name</p>
