import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Progress } from "@/components/ui/progress";
import { 
  Fingerprint, 
  Shield, 
  CheckCircle, 
  AlertTriangle, 
  Loader2, 
  Building2,
  User,
  FileText,
  Clock,
  Phone,
  MessageSquare,
  Smartphone,
  Download
} from "lucide-react";
import { generateClaimForm, downloadPDF, generateFilename, type PatientData, type ClaimService } from "@/utils/autofillClaim";
// Temporary mock biometric service
const mockBiometricService = {
  async simulateFingerprintScan(patientId: string) {
    await new Promise(resolve => setTimeout(resolve, 2000));
    return {
      fingerprintHash: `fp_${patientId}_${Date.now()}`,
      deviceFingerprint: `device_${Date.now()}`,
      timestamp: Date.now(),
      sessionId: `session_${Date.now()}`
    };
  },
  async storeBiometricData(patientId: string, data: any) {
    localStorage.setItem(`biometric_${patientId}`, JSON.stringify(data));
  }
};
import { EnhancedClaimTracker } from "@/components/enhanced-claim-tracker";

interface InsurancePolicy {
  id: string;
  insurerName: string;
  schemeName: string;
  policyNumber: string;
  coverageAmount: number;
  expiryDate: string;
  isActive: boolean;
}

interface ActiveSession {
  sessionId: string;
  facilityName: string;
  serviceName: string;
  startedAt: string;
  userId: string;
  userRole: string;
}

interface Patient {
  id: number;
  patientId: string;
  firstName: string;
  lastName: string;
  dateOfBirth: string;
  gender: string;
  phoneNumber?: string;
  biometricId?: string;
}

export default function FrontDeskPage() {
  const [step, setStep] = useState<'input' | 'verification' | 'selection' | 'session' | 'service' | 'preauth' | 'complete'>('input');
  const [patientId, setPatientId] = useState('');
  const [otpCode, setOtpCode] = useState('');
  const [verificationMethod, setVerificationMethod] = useState<'fingerprint' | 'otp'>('fingerprint');

  // Biometric states
  const [isScanning, setIsScanning] = useState(false);
  const [scanProgress, setScanProgress] = useState(0);
  const [verificationStatus, setVerificationStatus] = useState<'idle' | 'scanning' | 'success' | 'failed'>('idle');

  // Patient data
  const [verifiedPatient, setVerifiedPatient] = useState<Patient | null>(null);
  const [activePolicies, setActivePolicies] = useState<InsurancePolicy[]>([]);
  const [selectedInsurer, setSelectedInsurer] = useState<InsurancePolicy | null>(null);

  // Session management
  const [activeSessions, setActiveSessions] = useState<ActiveSession[]>([]);
  const [showSessionModal, setShowSessionModal] = useState(false);
  const [sessionToHandle, setSessionToHandle] = useState<ActiveSession | null>(null);

  // SMS OTP states
  const [isSendingOtp, setIsSendingOtp] = useState(false);
  const [otpSent, setOtpSent] = useState(false);

  // Service selection and preauthorization states
  const [selectedService, setSelectedService] = useState<string>('');
  const [benefitBuckets, setBenefitBuckets] = useState<any[]>([]);
  const [preauthResult, setPreauthResult] = useState<any>(null);
  const [preauthLoading, setPreauthLoading] = useState(false);

  const handlePatientLookup = async () => {
    if (!patientId.trim()) return;

    setStep('verification');
    setVerificationStatus('idle');
  };

  const handleFingerprintVerification = async () => {
    setIsScanning(true);
    setVerificationStatus('scanning');
    setScanProgress(0);

    try {
      // Generate realistic biometric scan
      const biometricData = await mockBiometricService.simulateFingerprintScan(patientId);

      // Simulate progressive scanning
      const progressInterval = setInterval(() => {
        setScanProgress(prev => {
          if (prev >= 100) {
            clearInterval(progressInterval);
            return 100;
          }
          return prev + Math.random() * 12 + 3;
        });
      }, 180);

      // Wait for scanning completion
      await new Promise(resolve => {
        const checkProgress = () => {
          if (scanProgress >= 100) {
            clearInterval(progressInterval);
            resolve(true);
          } else {
            setTimeout(checkProgress, 100);
          }
        };
        checkProgress();
      });

      // Store biometric data
      await mockBiometricService.storeBiometricData(patientId, biometricData);

      // Verify patient with biometric data
      await verifyPatientIdentity(biometricData);

    } catch (error) {
      console.error('Fingerprint verification error:', error);
      setVerificationStatus('failed');
    } finally {
      setIsScanning(false);
    }
  };

  const handleSendOtp = async () => {
    setIsSendingOtp(true);

    try {
      // Simulate SMS sending delay
      await new Promise(resolve => setTimeout(resolve, 2000));

      // In production, this would call SMS API
      console.log(`Sending OTP to patient ${patientId}`);

      setOtpSent(true);
    } catch (error) {
      console.error('OTP sending failed:', error);
    } finally {
      setIsSendingOtp(false);
    }
  };

  const handleOtpVerification = async () => {
    if (!otpCode.trim() || otpCode.length !== 6) {
      return;
    }

    setVerificationStatus('scanning');

    try {
      // Simulate OTP verification
      await new Promise(resolve => setTimeout(resolve, 1500));

      // For demo, accept any 6-digit code
      if (otpCode.length === 6) {
        await verifyPatientIdentity(null, otpCode);
      } else {
        setVerificationStatus('failed');
      }
    } catch (error) {
      console.error('OTP verification error:', error);
      setVerificationStatus('failed');
    }
  };

  const verifyPatientIdentity = async (biometricData?: any, otp?: string) => {
    try {
      const response = await fetch(`/api/front-desk/verify-patient`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          patientId,
          biometricData,
          otpCode: otp
        })
      });

      const data = await response.json();

      if (data.verified) {
        setVerifiedPatient(data.patient);
        setActivePolicies(data.activePolicies || []);
        setActiveSessions(data.activeSessions || []);
        setVerificationStatus('success');

        // Check for active sessions at other facilities
        if (data.activeSessions && data.activeSessions.length > 0) {
          setSessionToHandle(data.activeSessions[0]);
          setShowSessionModal(true);
        } else if (data.activePolicies && data.activePolicies.length > 1) {
          // Multiple insurers - show selection
          setStep('selection');
        } else if (data.activePolicies && data.activePolicies.length === 1) {
          // Single insurer - proceed directly
          setSelectedInsurer(data.activePolicies[0]);
          setStep('complete');
        } else {
          // No active policies
          setStep('complete');
        }
      } else {
        setVerificationStatus('failed');
      }
    } catch (error) {
      console.error('Patient verification error:', error);
      setVerificationStatus('failed');
    }
  };

  const handleInsurerSelection = (insurerId: string) => {
    const selected = activePolicies.find(policy => policy.id === insurerId);
    if (selected) {
      setSelectedInsurer(selected);
      setStep('complete');

      // Store selection in session
      sessionStorage.setItem('selectedInsurer', JSON.stringify(selected));
    }
  };

  const handleContinueSession = async (continueSession: boolean) => {
    setShowSessionModal(false);

    if (continueSession && sessionToHandle) {
      // Continue existing session
      try {
        await fetch('/api/front-desk/continue-session', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
          body: JSON.stringify({
            sessionId: sessionToHandle.sessionId,
            patientId: verifiedPatient?.patientId
          })
        });

        setStep('complete');
      } catch (error) {
        console.error('Session continuation error:', error);
      }
    } else {
      // Start new session - check for multiple insurers
      if (activePolicies.length > 1) {
        setStep('selection');
      } else if (activePolicies.length === 1) {
        setSelectedInsurer(activePolicies[0]);
        setStep('complete');
      } else {
        setStep('complete');
      }
    }
  };

  const handleStartNewClaim = async () => {
    if (!verifiedPatient || !selectedInsurer) return;

    // Mock benefit buckets for the patient's insurance
    const mockBenefits = [
      {
        serviceType: 'consultation',
        name: 'Medical Consultation',
        description: 'General consultation with doctor',
        totalAllowed: 12,
        usedCount: 3,
        remainingAmount: 'KES 45,000',
        icon: 'ü©∫'
      },
      {
        serviceType: 'laboratory',
        name: 'Laboratory Tests',
        description: 'Blood tests, X-rays, and diagnostics',
        totalAllowed: 20,
        usedCount: 5,
        remainingAmount: 'KES 25,000',
        icon: 'üß™'
      },
      {
        serviceType: 'pharmacy',
        name: 'Pharmacy Benefits',
        description: 'Prescription medications',
        totalAllowed: 24,
        usedCount: 8,
        remainingAmount: 'KES 18,000',
        icon: 'üíä'
      },
      {
        serviceType: 'specialist',
        name: 'Specialist Consultation',
        description: 'Cardiologist, Dermatologist, etc.',
        totalAllowed: 6,
        usedCount: 1,
        remainingAmount: 'KES 30,000',
        icon: 'üë®‚Äç‚öïÔ∏è'
      }
    ];

    setBenefitBuckets(mockBenefits);
    setStep('service');
  };

  const handleServiceSelection = async (serviceType: string) => {
    setSelectedService(serviceType);
    setStep('preauth');
    setPreauthLoading(true);

    try {
      const response = await fetch('/api/preauth', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          patientId: verifiedPatient?.id,
          insurer: selectedInsurer?.insurerName,
          serviceType: serviceType,
          clinicalJustification: `${serviceType} service requested for patient ${verifiedPatient?.patientId}`,
          estimatedCost: getEstimatedCost(serviceType),
          urgency: 'routine'
        })
      });

      const result = await response.json();
      setPreauthResult(result);

      if (result.decision === 'approved') {
        // Success - allow billing
        setStep('complete');
      } else {
        // Show denial reason
        // Stay on preauth step to show the denial
      }
    } catch (error) {
      console.error('Preauthorization error:', error);
      setPreauthResult({
        decision: 'denied',
        reason: 'System error during preauthorization. Please try again.',
        confidence: 0
      });
    } finally {
      setPreauthLoading(false);
    }
  };

  const getEstimatedCost = (serviceType: string): string => {
    const costs = {
      consultation: '2500',
      laboratory: '4500',
      pharmacy: '3200',
      specialist: '5000'
    };
    return costs[serviceType as keyof typeof costs] || '2000';
  };

  const generatePatientClaimForm = async () => {
    if (!verifiedPatient || !selectedInsurer) return;

    try {
      const patientData: PatientData = {
        name: `${verifiedPatient.firstName} ${verifiedPatient.lastName}`,
        dateOfBirth: new Date(verifiedPatient.dateOfBirth).toLocaleDateString(),
        gender: verifiedPatient.gender,
        memberId: verifiedPatient.patientId,
        phoneNumber: verifiedPatient.phoneNumber || '',
        address: verifiedPatient.address || '',
        diagnosis: 'General Consultation',
        treatment: 'Medical Evaluation',
        serviceDate: new Date().toLocaleDateString(),
        providerName: 'Erlessed Healthcare Platform'
      };

      const sampleServices: ClaimService[] = [
        {
          serviceCode: 'CONS001',
          serviceName: 'General Consultation',
          quantity: 1,
          unitCost: 2000,
          totalCost: 2000,
          serviceDate: new Date().toLocaleDateString()
        }
      ];

      const pdfBytes = await generateClaimForm(
        patientData, 
        selectedInsurer.insurerName, 
        sampleServices
      );

      const filename = generateFilename(patientData, selectedInsurer.insurerName);
      downloadPDF(pdfBytes, filename);

    } catch (error) {
      console.error('Error generating claim form:', error);
    }
  };

  const resetFlow = () => {
    setStep('input');
    setPatientId('');
    setOtpCode('');
    setVerificationStatus('idle');
    setVerifiedPatient(null);
    setActivePolicies([]);
    setSelectedInsurer(null);
    setActiveSessions([]);
    setScanProgress(0);
    setOtpSent(false);
    setSelectedService('');
    setBenefitBuckets([]);
    setPreauthResult(null);
    setPreauthLoading(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-4xl mx-auto space-y-6">
        {/* Header */}
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Front Office Patient Verification</h1>
          <p className="text-gray-600">Secure patient identity verification and claim session management</p>
        </div>

        {/* Progress Indicator */}
        <div className="flex items-center justify-center space-x-4 mb-8">
          <div className={`flex items-center space-x-2 ${step === 'input' ? 'text-teal-600' : step === 'verification' || step === 'selection' || step === 'complete' ? 'text-green-600' : 'text-gray-400'}`}>
            <div className={`w-8 h-8 rounded-full flex items-center justify-center ${step === 'input' ? 'bg-teal-100 border-2 border-teal-600' : step === 'verification' || step === 'selection' || step === 'complete' ? 'bg-green-100 border-2 border-green-600' : 'bg-gray-100 border-2 border-gray-300'}`}>
              <User className="w-4 h-4" />
            </div>
            <span className="text-sm font-medium">Patient ID</span>
          </div>

          <div className={`w-8 h-1 ${step === 'verification' || step === 'selection' || step === 'complete' ? 'bg-green-600' : 'bg-gray-300'}`}></div>

          <div className={`flex items-center space-x-2 ${step === 'verification' ? 'text-teal-600' : step === 'selection' || step === 'complete' ? 'text-green-600' : 'text-gray-400'}`}>
            <div className={`w-8 h-8 rounded-full flex items-center justify-center ${step === 'verification' ? 'bg-teal-100 border-2 border-teal-600' : step === 'selection' || step === 'complete' ? 'bg-green-100 border-2 border-green-600' : 'bg-gray-100 border-2 border-gray-300'}`}>
              <Shield className="w-4 h-4" />
            </div>
            <span className="text-sm font-medium">Verification</span>
          </div>

          <div className={`w-8 h-1 ${step === 'complete' ? 'bg-green-600' : 'bg-gray-300'}`}></div>

          <div className={`flex items-center space-x-2 ${step === 'complete' ? 'text-green-600' : 'text-gray-400'}`}>
            <div className={`w-8 h-8 rounded-full flex items-center justify-center ${step === 'complete' ? 'bg-green-100 border-2 border-green-600' : 'bg-gray-100 border-2 border-gray-300'}`}>
              <CheckCircle className="w-4 h-4" />
            </div>
            <span className="text-sm font-medium">Complete</span>
          </div>
        </div>

        {/* Step 1: Patient ID Input */}
        {step === 'input' && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <User className="w-5 h-5 text-blue-600" />
                Patient Identification
              </CardTitle>
              <CardDescription>
                Enter the patient ID to begin verification process
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-2">Patient ID</label>
                <Input
                  value={patientId}
                  onChange={(e) => setPatientId(e.target.value)}
                  placeholder="Enter patient ID (e.g., PAT001)"
                  className="text-lg"
                />
              </div>

              <Button 
                onClick={handlePatientLookup}
                disabled={!patientId.trim()}
                className="w-full bg-teal-600 hover:bg-teal-700"
              >
                Lookup Patient
              </Button>
            </CardContent>
          </Card>
        )}

        {/* Step 2: Verification Method Selection and Execution */}
        {step === 'verification' && (
          <div className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Shield className="w-5 h-5 text-teal-600" />
                  Identity Verification
                </CardTitle>
                <CardDescription>
                  Choose verification method for patient {patientId}
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Verification Method Selection */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <Button
                    variant={verificationMethod === 'fingerprint' ? 'default' : 'outline'}
                    onClick={() => setVerificationMethod('fingerprint')}
                    className="h-20 flex flex-col items-center justify-center space-y-2"
                  >
                    <Fingerprint className="w-6 h-6" />
                    <span>Fingerprint Scan</span>
                  </Button>

                  <Button
                    variant={verificationMethod === 'otp' ? 'default' : 'outline'}
                    onClick={() => setVerificationMethod('otp')}
                    className="h-20 flex flex-col items-center justify-center space-y-2"
                  >
                    <Smartphone className="w-6 h-6" />
                    <span>SMS OTP</span>
                  </Button>
                </div>

                {/* Fingerprint Verification */}
                {verificationMethod === 'fingerprint' && (
                  <div className="text-center space-y-4">
                    {verificationStatus === 'idle' && (
                      <Button
                        onClick={handleFingerprintVerification}
                        className="bg-teal-600 hover:bg-teal-700"
                      >
                        <Fingerprint className="w-4 h-4 mr-2" />
                        Start Fingerprint Scan
                      </Button>
                    )}

                    {verificationStatus === 'scanning' && (
                      <div className="space-y-4">
                        <div className="flex items-center justify-center">
                          <div className="relative">
                            <Fingerprint className={`w-16 h-16 text-teal-600 ${isScanning ? 'animate-pulse' : ''}`} />
                            {isScanning && (
                              <Loader2 className="w-6 h-6 animate-spin absolute -top-1 -right-1 text-blue-600" />
                            )}
                          </div>
                        </div>
                        <div className="space-y-2">
                          <p className="text-sm text-gray-600">Scanning fingerprint...</p>
                          <Progress value={scanProgress} className="h-2" />
                          <p className="text-xs text-gray-500">{Math.round(scanProgress)}% complete</p>
                        </div>
                      </div>
                    )}

                    {verificationStatus === 'success' && (
                      <div className="text-center text-green-600">
                        <CheckCircle className="w-16 h-16 mx-auto mb-2" />
                        <p className="font-medium">Fingerprint Verified Successfully</p>
                      </div>
                    )}

                    {verificationStatus === 'failed' && (
                      <div className="text-center">
                        <AlertTriangle className="w-16 h-16 mx-auto mb-2 text-red-600" />
                        <p className="font-medium text-red-600">Verification Failed</p>
                        <Button
                          onClick={() => setVerificationStatus('idle')}
                          variant="outline"
                          className="mt-2"
                        >
                          Try Again
                        </Button>
                      </div>
                    )}
                  </div>
                )}

                {/* SMS OTP Verification */}
                {verificationMethod === 'otp' && (
                  <div className="space-y-4">
                    {!otpSent ? (
                      <div className="text-center">
                        <Button
                          onClick={handleSendOtp}
                          disabled={isSendingOtp}
                          className="bg-blue-600 hover:bg-blue-700"
                        >
                          {isSendingOtp ? (
                            <>
                              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                              Sending OTP...
                            </>
                          ) : (
                            <>
                              <MessageSquare className="w-4 h-4 mr-2" />
                              Send OTP
                            </>
                          )}
                        </Button>
                      </div>
                    ) : (
                      <div className="space-y-4">
                        <Alert>
                          <Phone className="w-4 h-4" />
                          <AlertDescription>
                            OTP has been sent to the patient's registered phone number
                          </AlertDescription>
                        </Alert>

                        <div>
                          <label className="block text-sm font-medium mb-2">Enter 6-digit OTP</label>
                          <Input
                            value={otpCode}
                            onChange={(e) => setOtpCode(e.target.value.replace(/\D/g, '').slice(0, 6))}
                            placeholder="000000"
                            className="text-center text-lg tracking-widest"
                            maxLength={6}
                          />
                        </div>

                        <Button
                          onClick={handleOtpVerification}
                          disabled={otpCode.length !== 6 || verificationStatus === 'scanning'}
                          className="w-full bg-blue-600 hover:bg-blue-700"
                        >
                          {verificationStatus === 'scanning' ? (
                            <>
                              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                              Verifying...
                            </>
                          ) : (
                            'Verify OTP'
                          )}
                        </Button>

                        {verificationStatus === 'failed' && (
                          <Alert>
                            <AlertTriangle className="w-4 h-4" />
                            <AlertDescription>
                              Invalid OTP. Please check and try again.
                            </AlertDescription>
                          </Alert>
                        )}
                      </div>
                    )}
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        )}

        {/* Step 3: Insurer Selection */}
        {step === 'selection' && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Building2 className="w-5 h-5 text-blue-600" />
                Insurance Provider Selection
              </CardTitle>
              <CardDescription>
                Patient {verifiedPatient?.firstName} {verifiedPatient?.lastName} has multiple active insurance policies
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid gap-4">
                {activePolicies.map((policy) => (
                  <Card 
                    key={policy.id}
                    className="cursor-pointer hover:shadow-md transition-shadow border-l-4 border-l-blue-500"
                    onClick={() => handleInsurerSelection(policy.id)}
                  >
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <h3 className="font-semibold text-lg">{policy.insurerName}</h3>
                          <p className="text-gray-600">{policy.schemeName}</p>
                          <p className="text-sm text-gray-500">Policy: {policy.policyNumber}</p>
                        </div>
                        <div className="text-right">
                          <Badge className="bg-green-100 text-green-800 mb-2">Active</Badge>
                          <p className="text-sm font-medium">
                            KES {policy.coverageAmount.toLocaleString()}
                          </p>
                          <p className="text-xs text-gray-500">
                            Expires: {new Date(policy.expiryDate).toLocaleDateString()}
                          </p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Step 4: Service Selection */}
        {step === 'service' && verifiedPatient && selectedInsurer && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="w-5 h-5 text-teal-600" />
                Select Service Type
              </CardTitle>
              <CardDescription>
                Choose the type of service for {verifiedPatient.firstName} {verifiedPatient.lastName}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {benefitBuckets.map((bucket) => {
                  const usagePercent = (bucket.usedCount / bucket.totalAllowed) * 100;
                  const isLowUsage = usagePercent < 50;
                  const isMediumUsage = usagePercent >= 50 && usagePercent < 80;
                  const isHighUsage = usagePercent >= 80;

                  return (
                    <Card 
                      key={bucket.serviceType}
                      className="cursor-pointer hover:shadow-md transition-all border-l-4 border-l-teal-500 hover:border-l-teal-600"
                      onClick={() => handleServiceSelection(bucket.serviceType)}
                    >
                      <CardContent className="p-4">
                        <div className="flex items-start justify-between mb-3">
                          <div className="flex items-center gap-3">
                            <span className="text-2xl">{bucket.icon}</span>
                            <div>
                              <h3 className="font-semibold text-lg">{bucket.name}</h3>
                              <p className="text-sm text-gray-600">{bucket.description}</p>
                            </div>
                          </div>
                          <Badge 
                            className={`${
                              isLowUsage ? 'bg-green-100 text-green-800' :
                              isMediumUsage ? 'bg-yellow-100 text-yellow-800' :
                              'bg-red-100 text-red-800'
                            }`}
                          >
                            {bucket.usedCount}/{bucket.totalAllowed}
                          </Badge>
                        </div>

                        <div className="space-y-2">
                          <div className="flex justify-between text-sm">
                            <span>Usage</span>
                            <span>{Math.round(usagePercent)}%</span>
                          </div>
                          <Progress 
                            value={usagePercent} 
                            className={`h-2 ${
                              isLowUsage ? '[&>div]:bg-green-500' :
                              isMediumUsage ? '[&>div]:bg-yellow-500' :
                              '[&>div]:bg-red-500'
                            }`}
                          />
                          <p className="text-sm font-medium text-teal-700">
                            Remaining: {bucket.remainingAmount}
                          </p>
                        </div>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>

              <div className="mt-6 pt-4 border-t">
                <Button 
                  onClick={() => setStep('complete')} 
                  variant="outline" 
                  className="w-full"
                >
                  Back to Patient Summary
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Step 5: Preauthorization */}
        {step === 'preauth' && verifiedPatient && selectedService && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Shield className="w-5 h-5 text-blue-600" />
                Preauthorization Request
              </CardTitle>
              <CardDescription>
                Processing preauthorization for {selectedService} service
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              {preauthLoading ? (
                <div className="text-center space-y-4">
                  <Loader2 className="w-16 h-16 mx-auto animate-spin text-blue-600" />
                  <div>
                    <p className="text-lg font-medium">Processing Authorization...</p>
                    <p className="text-sm text-gray-600">Analyzing patient eligibility and coverage</p>
                  </div>
                </div>
              ) : preauthResult ? (
                <div className="space-y-4">
                  {preauthResult.decision === 'approved' ? (
                    <Alert className="border-green-200 bg-green-50">
                      <CheckCircle className="w-4 h-4 text-green-600" />
                      <AlertDescription className="text-green-800">
                        <strong>Preauthorization Approved</strong>
                        <br />
                        {preauthResult.reason || 'Service authorized for billing.'}
                        <br />
                        <span className="text-sm">Confidence: {Math.round((preauthResult.confidence || 0.95) * 100)}%</span>
                      </AlertDescription>
                    </Alert>
                  ) : (
                    <Alert className="border-red-200 bg-red-50">
                      <AlertTriangle className="w-4 h-4 text-red-600" />
                      <AlertDescription className="text-red-800">
                        <strong>Preauthorization Denied</strong>
                        <br />
                        {preauthResult.reason || 'Service not authorized at this time.'}
                        <br />
                        <span className="text-sm">Confidence: {Math.round((preauthResult.confidence || 0.85) * 100)}%</span>
                      </AlertDescription>
                    </Alert>
                  )}

                  {preauthResult.chainOfThought && (
                    <Card className="mt-4">
                      <CardHeader>
                        <CardTitle className="text-sm">Decision Process</CardTitle>
                      </CardHeader>
                      <CardContent>
                        <div className="space-y-2 text-sm">
                          {preauthResult.chainOfThought.map((step: any, index: number) => (
                            <div key={index} className="flex items-start gap-2">
                              <span className="text-blue-600 font-mono text-xs mt-1">{index + 1}.</span>
                              <span className="text-gray-700">{step}</span>
                            </div>
                          ))}
                        </div>
                      </CardContent>
                    </Card>
                  )}

                  <div className="flex gap-4 pt-4">
                    <Button 
                      onClick={() => setStep('service')} 
                      variant="outline" 
                      className="flex-1"
                    >
                      Select Different Service
                    </Button>
                    {preauthResult.decision === 'approved' ? (
                      <Button 
                        onClick={() => setStep('complete')} 
                        className="flex-1 bg-green-600 hover:bg-green-700"
                      >
                        Proceed to Billing
                      </Button>
                    ) : (
                      <Button 
                        onClick={() => setStep('complete')} 
                        variant="outline" 
                        className="flex-1"
                      >
                        Return to Summary
                      </Button>
                    )}
                  </div>
                </div>
              ) : null}
            </CardContent>
          </Card>
        )}

        {/* Step 6: Complete */}
        {step === 'complete' && verifiedPatient && (
          <div className="space-y-6">
            {/* Patient Summary */}
            <Card className="border-l-4 border-l-green-500">
              <CardHeader>
                <CardTitle className="flex items-center gap-2 text-green-700">
                  <CheckCircle className="w-5 h-5" />
                  Patient Verified Successfully
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <h3 className="font-semibold text-lg">
                      {verifiedPatient.firstName} {verifiedPatient.lastName}
                    </h3>
                    <p className="text-gray-600">ID: {verifiedPatient.patientId}</p>
                    <p className="text-gray-600">DOB: {new Date(verifiedPatient.dateOfBirth).toLocaleDateString()}</p>
                    <p className="text-gray-600">Gender: {verifiedPatient.gender}</p>
                  </div>

                  {selectedInsurer && (
                    <div>
                      <h4 className="font-medium text-gray-700 mb-2">Selected Insurance</h4>
                      <div className="bg-blue-50 p-3 rounded-lg">
                        <p className="font-medium">{selectedInsurer.insurerName}</p>
                        <p className="text-sm text-gray-600">{selectedInsurer.schemeName}</p>
                        <p className="text-sm text-gray-600">Coverage: KES {selectedInsurer.coverageAmount.toLocaleString()}</p>
                      </div>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Enhanced Claims Tracker */}
            <EnhancedClaimTracker patientId={verifiedPatient.patientId} showAllClaims={false} />

            {/* Action Buttons */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <Button onClick={resetFlow} variant="outline">
                Verify New Patient
              </Button>
              {selectedInsurer && (
                <Button 
                  onClick={generatePatientClaimForm}
                  className="bg-blue-600 hover:bg-blue-700"
                >
                  <Download className="w-4 h-4 mr-2" />
                  Generate Claim Form
                </Button>
              )}
              <Button 
                onClick={handleStartNewClaim}
                className="bg-teal-600 hover:bg-teal-700"
              >
                <FileText className="w-4 h-4 mr-2" />
                Start New Claim
              </Button>
            </div>
          </div>
        )}

        {/* Active Session Modal */}
        {showSessionModal && sessionToHandle && (
          <Dialog open={showSessionModal} onOpenChange={setShowSessionModal}>
            <DialogContent>
              <DialogHeader>
                <DialogTitle className="flex items-center gap-2">
                  <AlertTriangle className="w-5 h-5 text-orange-600" />
                  Active Session Detected
                </DialogTitle>
                <DialogDescription>
                  An active claim session was found at another facility
                </DialogDescription>
              </DialogHeader>

              <div className="space-y-4">
                <Alert>
                  <Building2 className="w-4 h-4" />
                  <AlertDescription>
                    <strong>Facility:</strong> {sessionToHandle.facilityName}<br />
                    <strong>Service:</strong> {sessionToHandle.serviceName}<br />
                    <strong>Started:</strong> {new Date(sessionToHandle.startedAt).toLocaleString()}<br />
                    <strong>Provider:</strong> {sessionToHandle.userRole}
                  </AlertDescription>
                </Alert>

                <p className="text-sm text-gray-600">
                  Would you like to continue the existing session or start a new one?
                </p>
              </div>

              <DialogFooter className="gap-2">
                <Button 
                  variant="outline" 
                  onClick={() => handleContinueSession(false)}
                >
                  Start New Session
                </Button>
                <Button 
                  onClick={() => handleContinueSession(true)}
                  className="bg-orange-600 hover:bg-orange-700"
                >
                  <Clock className="w-4 h-4 mr-2" />
                  Continue Existing
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        )}
      </div>
    </div>
  );
}

client/src/pages/home-page.tsx.content:
import { useAuth } from "@/hooks/use-auth";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  FileText, Brain, Users, TrendingUp, Clock, Shield, 
  Activity, DollarSign, AlertTriangle, CheckCircle 
} from "lucide-react";
import { Link } from "wouter";

interface DashboardStats {
  analytics: {
    totalClaims: number;
    totalAmount: number;
    avgProcessingTime: number;
    approvalRate: number;
  };
  recentDecisions: Array<{
    id: number;
    decision: string;
    confidence: number;
    requestType: string;
    createdAt: string;
  }>;
  fraudAlerts: number;
  topProviders: Array<{
    providerId: string;
    totalAmount: number;
    approvalRate: number;
  }>;
}

export default function HomePage() {
  const { user } = useAuth();

  const { data: dashboardStats, isLoading } = useQuery<DashboardStats>({
    queryKey: ["/api/dashboard/stats"],
  });

  const getRoleBasedGreeting = () => {
    switch (user?.role) {
      case 'front-office':
        return "Welcome to the Front Office Dashboard";
      case 'doctor':
        return "Welcome to the Physician Dashboard";
      case 'lab':
        return "Welcome to the Laboratory Dashboard";
      case 'pharmacy':
        return "Welcome to the Pharmacy Dashboard";
      case 'debtors':
        return "Welcome to the Debtors Management Dashboard";
      case 'care-manager':
        return "Welcome to the Care Manager Dashboard";
      default:
        return "Welcome to Erlessed";
    }
  };

  const getRoleSpecificActions = () => {
    switch (user?.role) {
      case 'front-office':
        return [
          { label: "Verify Patient", href: "/verification", icon: Shield },
          { label: "New Claim", href: "/claims", icon: FileText },
          { label: "View Analytics", href: "/analytics", icon: TrendingUp },
        ];
      case 'doctor':
        return [
          { label: "Patient Verification", href: "/verification", icon: Shield },
          { label: "Submit Claim", href: "/claims", icon: FileText },
          { label: "AI Preauth", href: "/preauth", icon: Brain },
        ];
      case 'pharmacy':
        return [
          { label: "Prescription Validation", href: "/pharmacy", icon: Shield },
          { label: "Drug Interaction Check", href: "/pharmacy", icon: AlertTriangle },
          { label: "Benefit Categories", href: "/pharmacy", icon: Activity },
        ];
      case 'care-manager':
        return [
          { label: "Analytics Dashboard", href: "/analytics", icon: TrendingUp },
          { label: "Provider Performance", href: "/analytics", icon: Users },
          { label: "Fraud Detection", href: "/analytics", icon: AlertTriangle },
        ];
      default:
        return [
          { label: "Patient Verification", href: "/verification", icon: Shield },
          { label: "Claims Processing", href: "/claims", icon: FileText },
          { label: "AI Decisions", href: "/preauth", icon: Brain },
        ];
    }
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 rounded w-1/4"></div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-32 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">{getRoleBasedGreeting()}</h1>
          <p className="text-gray-600">
            {user?.department && `${user.department} ‚Ä¢ `}
            Role: {user?.role?.replace('-', ' ').replace(/\b\w/g, l => l.toUpperCase())}
          </p>
        </div>
        <div className="flex items-center space-x-2">
          <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
          <span className="text-sm text-gray-600">System Online</span>
        </div>
      </div>

      {/* Key Metrics */}
      {dashboardStats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Claims</CardTitle>
              <FileText className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{dashboardStats.analytics.totalClaims?.toLocaleString() || 0}</div>
              <p className="text-xs text-muted-foreground">
                Active processing pipeline
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">AI Approval Rate</CardTitle>
              <Brain className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{dashboardStats.analytics.approvalRate?.toFixed(1) || 0}%</div>
              <p className="text-xs text-muted-foreground">
                Chain-of-thought decisions
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Processing Time</CardTitle>
              <Clock className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{dashboardStats.analytics.avgProcessingTime?.toFixed(1) || 0}s</div>
              <p className="text-xs text-muted-foreground">
                Average decision time
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Value</CardTitle>
              <DollarSign className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">${(dashboardStats.analytics.totalAmount || 0).toLocaleString()}</div>
              <p className="text-xs text-muted-foreground">
                Claims processed
              </p>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Quick Actions */}
      <Card>
        <CardHeader>
          <CardTitle>Quick Actions</CardTitle>
          <CardDescription>
            Common tasks for your role
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {getRoleSpecificActions().map((action, index) => (
              <Link key={index} href={action.href}>
                <Button
                  variant="outline"
                  className="w-full h-20 flex flex-col items-center justify-center space-y-2 hover:bg-teal-50 hover:border-teal-300"
                >
                  <action.icon className="w-6 h-6 text-teal-600" />
                  <span>{action.label}</span>
                </Button>
              </Link>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Recent AI Decisions */}
      {dashboardStats?.recentDecisions && dashboardStats.recentDecisions.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Brain className="w-5 h-5 text-purple-600" />
              <span>Recent AI Decisions</span>
            </CardTitle>
            <CardDescription>
              Latest automated preauthorization decisions
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {dashboardStats.recentDecisions.slice(0, 5).map((decision) => (
                <div key={decision.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                  <div className="flex items-center space-x-3">
                    <div className={`w-3 h-3 rounded-full ${
                      decision.decision === 'approved' ? 'bg-green-500' : 
                      decision.decision === 'denied' ? 'bg-red-500' : 
                      'bg-yellow-500'
                    }`} />
                    <div>
                      <p className="font-medium text-sm">{decision.requestType.replace('_', ' ').toUpperCase()}</p>
                      <p className="text-xs text-gray-500">
                        {new Date(decision.createdAt).toLocaleString()}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Badge variant={
                      decision.decision === 'approved' ? 'default' : 
                      decision.decision === 'denied' ? 'destructive' : 
                      'secondary'
                    }>
                      {decision.decision.toUpperCase()}
                    </Badge>
                    <span className="text-sm text-gray-500">{decision.confidence?.toFixed(1)}%</span>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Fraud Alerts */}
      {dashboardStats?.fraudAlerts && dashboardStats.fraudAlerts > 0 && (
        <Alert className="border-red-200 bg-red-50">
          <AlertTriangle className="h-4 w-4 text-red-600" />
          <AlertDescription className="text-red-800">
            <strong>{dashboardStats.fraudAlerts} fraud alert{dashboardStats.fraudAlerts !== 1 ? 's' : ''}</strong> detected in the last 24 hours. 
            <Link href="/analytics" className="underline ml-1">Review suspicious activity</Link>
          </AlertDescription>
        </Alert>
      )}

      {/* System Status */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Activity className="w-5 h-5 text-green-600" />
            <span>System Status</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="flex items-center space-x-3">
              <CheckCircle className="w-5 h-5 text-green-500" />
              <div>
                <p className="font-medium">AI Engine</p>
                <p className="text-sm text-gray-500">Operational</p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <CheckCircle className="w-5 h-5 text-green-500" />
              <div>
                <p className="font-medium">Blockchain Network</p>
                <p className="text-sm text-gray-500">Sepolia Connected</p>
              </div>
            </div>
            <div className="flex items-center space-x-3">
              <CheckCircle className="w-5 h-5 text-green-500" />
              <div>
                <p className="font-medium">Database</p>
                <p className="text-sm text-gray-500">Healthy</p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/insurer-dashboard.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Separator } from "@/components/ui/separator";
import { Shield, TrendingUp, AlertTriangle, DollarSign, Clock, FileText, Settings } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { LogoutButton } from "@/components/ui/logout-button";

interface PreauthorizationRequest {
  id: number;
  patient: {
    firstName: string;
    lastName: string;
    patientId: string;
    age: number;
    gender: string;
    membershipNumber: string;
    scheme: string;
  };
  provider: {
    name: string;
    type: string;
    riskScore: number;
  };
  serviceType: string;
  procedureCode: string;
  diagnosis: string;
  icd10Code: string;
  clinicalJustification: string;
  estimatedCost: number;
  urgency: "routine" | "urgent" | "emergency";
  requestedBy: string;
  submittedAt: string;
  aiDecision: "approved" | "denied" | "review_required";
  aiConfidence: number;
  aiReasoning: string[];
  status: "pending" | "approved" | "denied" | "review_required";
}

interface ClaimInflow {
  providerId: number;
  providerName: string;
  todayClaims: number;
  weekClaims: number;
  monthClaims: number;
  averageClaimValue: number;
  flaggedPercentage: number;
  lastClaimTime: string;
}

interface SchemeUsage {
  schemeId: number;
  schemeName: string;
  totalMembers: number;
  activeMembers: number;
  totalVisits: number;
  benefitUtilization: number;
  burnoutRate: number;
  averageCostPerMember: number;
}

export default function InsurerDashboard() {
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState("preauth");
  const [selectedRequest, setSelectedRequest] = useState<PreauthorizationRequest | null>(null);
  const [reviewNotes, setReviewNotes] = useState("");
  const [decision, setDecision] = useState<"approved" | "denied" | "">("");

  // Fetch pending preauthorization requests
  const { data: preauthorizationRequests = [] } = useQuery({
    queryKey: ["/api/insurer/preauthorizations/pending"],
    queryFn: async () => [
      {
        id: 1,
        patient: {
          firstName: "John",
          lastName: "Doe",
          patientId: "P001234",
          age: 45,
          gender: "Male",
          membershipNumber: "NHIF123456789",
          scheme: "NHIF Principal"
        },
        provider: {
          name: "Aga Khan University Hospital",
          type: "Hospital",
          riskScore: 2.1
        },
        serviceType: "Cardiac Catheterization",
        procedureCode: "93458",
        diagnosis: "Coronary Artery Disease",
        icd10Code: "I25.10",
        clinicalJustification: "Patient presents with chest pain and abnormal stress test. ECG shows ST depression. Requires cardiac catheterization to assess coronary anatomy and determine need for intervention.",
        estimatedCost: 450000,
        urgency: "urgent" as const,
        requestedBy: "Dr. Sarah Wilson",
        submittedAt: "2024-06-18T09:30:00Z",
        aiDecision: "review_required" as const,
        aiConfidence: 75,
        aiReasoning: [
          "High-cost procedure requires careful review",
          "Patient age and symptoms support clinical indication", 
          "Provider has good track record for this procedure",
          "Previous similar cases were medically necessary"
        ],
        status: "pending" as const
      },
      {
        id: 2,
        patient: {
          firstName: "Mary",
          lastName: "Smith",
          patientId: "P001235",
          age: 32,
          gender: "Female",
          membershipNumber: "CORP987654321",
          scheme: "Corporate Health Plan"
        },
        provider: {
          name: "Kenyatta National Hospital",
          type: "Hospital",
          riskScore: 3.2
        },
        serviceType: "MRI Brain",
        procedureCode: "70553",
        diagnosis: "Chronic Headaches",
        icd10Code: "R51",
        clinicalJustification: "Patient has persistent headaches for 3 months, not responding to medication. Neurological examination normal. MRI needed to rule out secondary causes.",
        estimatedCost: 25000,
        urgency: "routine" as const,
        requestedBy: "Dr. James Brown",
        submittedAt: "2024-06-18T08:15:00Z",
        aiDecision: "approved" as const,
        aiConfidence: 92,
        aiReasoning: [
          "Standard diagnostic protocol for chronic headaches",
          "Cost is within normal range for this procedure",
          "Clinical justification is appropriate",
          "No red flags in patient or provider history"
        ],
        status: "pending" as const
      }
    ] as PreauthorizationRequest[],
  });

  // Fetch claim inflow data
  const { data: claimInflow = [] } = useQuery({
    queryKey: ["/api/insurer/claims/inflow"],
    queryFn: async () => [
      {
        providerId: 1,
        providerName: "Aga Khan University Hospital",
        todayClaims: 45,
        weekClaims: 287,
        monthClaims: 1234,
        averageClaimValue: 18500,
        flaggedPercentage: 2.1,
        lastClaimTime: "2024-06-18T10:45:00Z"
      },
      {
        providerId: 2,
        providerName: "Kenyatta National Hospital",
        todayClaims: 67,
        weekClaims: 421,
        monthClaims: 1789,
        averageClaimValue: 15200,
        flaggedPercentage: 3.8,
        lastClaimTime: "2024-06-18T10:52:00Z"
      },
      {
        providerId: 3,
        providerName: "Carepoint Medical Centers",
        todayClaims: 123,
        weekClaims: 834,
        monthClaims: 3567,
        averageClaimValue: 4200,
        flaggedPercentage: 1.2,
        lastClaimTime: "2024-06-18T10:58:00Z"
      }
    ] as ClaimInflow[],
  });

  // Fetch scheme usage data
  const { data: schemeUsage = [] } = useQuery({
    queryKey: ["/api/insurer/schemes/usage"],
    queryFn: async () => [
      {
        schemeId: 1,
        schemeName: "NHIF Principal",
        totalMembers: 125000,
        activeMembers: 98500,
        totalVisits: 15600,
        benefitUtilization: 78.5,
        burnoutRate: 12.3,
        averageCostPerMember: 8500
      },
      {
        schemeId: 2,
        schemeName: "Corporate Health Plans",
        totalMembers: 45000,
        activeMembers: 42000,
        totalVisits: 8900,
        benefitUtilization: 65.2,
        burnoutRate: 8.7,
        averageCostPerMember: 15200
      }
    ] as SchemeUsage[],
  });

  const processPreauthorizationMutation = useMutation({
    mutationFn: async (data: {
      requestId: number;
      decision: "approved" | "denied";
      reviewNotes: string;
    }) => {
      return apiRequest("/api/insurer/preauthorizations/process", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
    },
    onSuccess: () => {
      toast({
        title: "Decision Recorded",
        description: "Preauthorization decision has been processed successfully",
      });
      setSelectedRequest(null);
      setReviewNotes("");
      setDecision("");
      queryClient.invalidateQueries({ queryKey: ["/api/insurer/preauthorizations/pending"] });
    },
  });

  const processDecision = () => {
    if (!selectedRequest || !decision) return;

    processPreauthorizationMutation.mutate({
      requestId: selectedRequest.id,
      decision,
      reviewNotes,
    });
  };

  const getUrgencyBadge = (urgency: string) => {
    const variants = {
      routine: "bg-green-100 text-green-800",
      urgent: "bg-yellow-100 text-yellow-800", 
      emergency: "bg-red-100 text-red-800"
    };
    return variants[urgency as keyof typeof variants] || "bg-gray-100 text-gray-800";
  };

  const getAIDecisionBadge = (decision: string) => {
    const variants = {
      approved: "bg-green-100 text-green-800",
      denied: "bg-red-100 text-red-800",
      review_required: "bg-yellow-100 text-yellow-800"
    };
    return variants[decision as keyof typeof variants] || "bg-gray-100 text-gray-800";
  };

  const getRiskScoreColor = (score: number) => {
    if (score >= 7) return "text-red-600";
    if (score >= 4) return "text-yellow-600";
    return "text-green-600";
  };

  const getConfidenceColor = (confidence: number) => {
    if (confidence >= 85) return "text-green-600";
    if (confidence >= 70) return "text-yellow-600";
    return "text-red-600";
  };

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Insurer Dashboard</h1>
          <p className="text-gray-600">Manage preauthorizations and monitor network activity</p>
        </div>
        <div className="flex items-center space-x-4">
          <Badge variant="outline" className="text-teal-600 border-teal-200">
            <Shield className="w-4 h-4 mr-1" />
            Insurer
          </Badge>
          <LogoutButton variant="dropdown" />
        </div>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="preauth">Preauthorizations</TabsTrigger>
          <TabsTrigger value="claims">Claims Inflow</TabsTrigger>
          <TabsTrigger value="schemes">Scheme Management</TabsTrigger>
          <TabsTrigger value="appeals">Appeals</TabsTrigger>
        </TabsList>

        <TabsContent value="preauth" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Pending Requests */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Clock className="w-5 h-5 mr-2" />
                  Pending Requests ({preauthorizationRequests.length})
                </CardTitle>
                <CardDescription>
                  AI-assisted preauthorization decisions
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {preauthorizationRequests.map((request) => (
                    <Card 
                      key={request.id} 
                      className={`cursor-pointer border-l-4 transition-colors ${
                        selectedRequest?.id === request.id 
                          ? "border-l-teal-500 bg-teal-50" 
                          : "border-l-gray-300 hover:border-l-teal-300"
                      }`}
                      onClick={() => setSelectedRequest(request)}
                    >
                      <CardContent className="p-4">
                        <div className="flex items-center justify-between mb-2">
                          <h3 className="font-semibold">
                            {request.patient.firstName} {request.patient.lastName}
                          </h3>
                          <Badge className={getUrgencyBadge(request.urgency)}>
                            {request.urgency}
                          </Badge>
                        </div>

                        <div className="space-y-1 text-sm text-gray-600">
                          <div>Service: {request.serviceType}</div>
                          <div>Provider: {request.provider.name}</div>
                          <div>Cost: KES {request.estimatedCost.toLocaleString()}</div>
                        </div>

                        <div className="flex items-center justify-between mt-3">
                          <Badge className={getAIDecisionBadge(request.aiDecision)}>
                            AI: {request.aiDecision.replace('_', ' ')}
                          </Badge>
                          <span className={`text-sm font-medium ${getConfidenceColor(request.aiConfidence)}`}>
                            {request.aiConfidence}% confidence
                          </span>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Decision Panel */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <FileText className="w-5 h-5 mr-2" />
                  {selectedRequest ? "Review Request" : "Select Request"}
                </CardTitle>
              </CardHeader>
              <CardContent>
                {selectedRequest ? (
                  <div className="space-y-6">
                    {/* Patient Info */}
                    <div>
                      <h3 className="font-semibold text-lg mb-3">Patient Information</h3>
                      <div className="grid grid-cols-2 gap-4 text-sm">
                        <div>
                          <span className="font-medium">Name:</span>
                          <div>{selectedRequest.patient.firstName} {selectedRequest.patient.lastName}</div>
                        </div>
                        <div>
                          <span className="font-medium">Member ID:</span>
                          <div>{selectedRequest.patient.membershipNumber}</div>
                        </div>
                        <div>
                          <span className="font-medium">Age/Gender:</span>
                          <div>{selectedRequest.patient.age} years, {selectedRequest.patient.gender}</div>
                        </div>
                        <div>
                          <span className="font-medium">Scheme:</span>
                          <div>{selectedRequest.patient.scheme}</div>
                        </div>
                      </div>
                    </div>

                    <Separator />

                    {/* Clinical Information */}
                    <div>
                      <h3 className="font-semibold text-lg mb-3">Clinical Information</h3>
                      <div className="space-y-3">
                        <div>
                          <span className="font-medium text-sm">Diagnosis:</span>
                          <div className="text-sm">{selectedRequest.diagnosis} ({selectedRequest.icd10Code})</div>
                        </div>
                        <div>
                          <span className="font-medium text-sm">Requested Service:</span>
                          <div className="text-sm">{selectedRequest.serviceType} (Code: {selectedRequest.procedureCode})</div>
                        </div>
                        <div>
                          <span className="font-medium text-sm">Clinical Justification:</span>
                          <div className="text-sm text-gray-700 bg-gray-50 p-3 rounded">
                            {selectedRequest.clinicalJustification}
                          </div>
                        </div>
                        <div>
                          <span className="font-medium text-sm">Estimated Cost:</span>
                          <div className="text-lg font-semibold text-teal-600">
                            KES {selectedRequest.estimatedCost.toLocaleString()}
                          </div>
                        </div>
                      </div>
                    </div>

                    <Separator />

                    {/* AI Analysis */}
                    <div>
                      <h3 className="font-semibold text-lg mb-3">AI Analysis</h3>
                      <div className="space-y-3">
                        <div className="flex items-center justify-between">
                          <Badge className={getAIDecisionBadge(selectedRequest.aiDecision)}>
                            {selectedRequest.aiDecision.replace('_', ' ').toUpperCase()}
                          </Badge>
                          <span className={`font-medium ${getConfidenceColor(selectedRequest.aiConfidence)}`}>
                            {selectedRequest.aiConfidence}% Confidence
                          </span>
                        </div>
                        <div>
                          <span className="font-medium text-sm">AI Reasoning:</span>
                          <ul className="mt-2 space-y-1">
                            {selectedRequest.aiReasoning.map((reason, index) => (
                              <li key={index} className="text-sm text-gray-700 flex items-start">
                                <span className="text-teal-500 mr-2">‚Ä¢</span>
                                {reason}
                              </li>
                            ))}
                          </ul>
                        </div>
                      </div>
                    </div>

                    <Separator />

                    {/* Decision Section */}
                    <div>
                      <h3 className="font-semibold text-lg mb-3">Your Decision</h3>
                      <div className="space-y-4">
                        <div>
                          <Label htmlFor="decision">Decision</Label>
                          <Select value={decision} onValueChange={setDecision}>
                            <SelectTrigger>
                              <SelectValue placeholder="Select decision" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="approved">Approve</SelectItem>
                              <SelectItem value="denied">Deny</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>

                        <div>
                          <Label htmlFor="reviewNotes">Review Notes</Label>
                          <Textarea
                            id="reviewNotes"
                            value={reviewNotes}
                            onChange={(e) => setReviewNotes(e.target.value)}
                            placeholder="Enter your review notes and justification"
                            rows={4}
                          />
                        </div>

                        <div className="flex justify-end space-x-2">
                          <Button 
                            variant="outline" 
                            onClick={() => setSelectedRequest(null)}
                          >
                            Cancel
                          </Button>
                          <Button
                            onClick={processDecision}
                            disabled={!decision || processPreauthorizationMutation.isPending}
                            className="bg-teal-600 hover:bg-teal-700"
                          >
                            {processPreauthorizationMutation.isPending ? "Processing..." : "Submit Decision"}
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <FileText className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-600">Select a preauthorization request to review</p>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        <TabsContent value="claims" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <TrendingUp className="w-5 h-5 mr-2" />
                Real-time Claims Inflow
              </CardTitle>
              <CardDescription>
                Monitor claim submission patterns across providers
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {claimInflow.map((provider) => (
                  <Card key={provider.providerId} className="border-l-4 border-l-blue-500">
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between mb-4">
                        <div>
                          <h3 className="font-semibold text-lg">{provider.providerName}</h3>
                          <p className="text-sm text-gray-600">
                            Last claim: {new Date(provider.lastClaimTime).toLocaleTimeString()}
                          </p>
                        </div>
                        <div className="text-right">
                          <div className="text-2xl font-bold text-blue-600">
                            {provider.todayClaims}
                          </div>
                          <p className="text-sm text-gray-600">Today</p>
                        </div>
                      </div>

                      <div className="grid grid-cols-4 gap-4">
                        <div className="text-center">
                          <div className="text-lg font-semibold">{provider.weekClaims}</div>
                          <p className="text-xs text-gray-600">This Week</p>
                        </div>
                        <div className="text-center">
                          <div className="text-lg font-semibold">{provider.monthClaims}</div>
                          <p className="text-xs text-gray-600">This Month</p>
                        </div>
                        <div className="text-center">
                          <div className="text-lg font-semibold">
                            KES {provider.averageClaimValue.toLocaleString()}
                          </div>
                          <p className="text-xs text-gray-600">Avg. Value</p>
                        </div>
                        <div className="text-center">
                          <div className={`text-lg font-semibold ${
                            provider.flaggedPercentage > 5 ? "text-red-600" : 
                            provider.flaggedPercentage > 2 ? "text-yellow-600" : "text-green-600"
                          }`}>
                            {provider.flaggedPercentage}%
                          </div>
                          <p className="text-xs text-gray-600">Flagged</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="schemes" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Settings className="w-5 h-5 mr-2" />
                Scheme Usage & Configuration
              </CardTitle>
              <CardDescription>
                Monitor benefit utilization and configure co-pay policies
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-6">
                {schemeUsage.map((scheme) => (
                  <Card key={scheme.schemeId} className="p-4">
                    <div className="flex items-center justify-between mb-4">
                      <h3 className="font-semibold text-lg">{scheme.schemeName}</h3>
                      <div className="text-right">
                        <div className="text-2xl font-bold text-teal-600">
                          {scheme.benefitUtilization}%
                        </div>
                        <p className="text-sm text-gray-600">Utilization</p>
                      </div>
                    </div>

                    <div className="grid grid-cols-4 gap-4 mb-4">
                      <div className="text-center">
                        <div className="text-lg font-semibold">{scheme.totalMembers.toLocaleString()}</div>
                        <p className="text-xs text-gray-600">Total Members</p>
                      </div>
                      <div className="text-center">
                        <div className="text-lg font-semibold">{scheme.activeMembers.toLocaleString()}</div>
                        <p className="text-xs text-gray-600">Active Members</p>
                      </div>
                      <div className="text-center">
                        <div className="text-lg font-semibold">{scheme.totalVisits.toLocaleString()}</div>
                        <p className="text-xs text-gray-600">Total Visits</p>
                      </div>
                      <div className="text-center">
                        <div className={`text-lg font-semibold ${
                          scheme.burnoutRate > 15 ? "text-red-600" : 
                          scheme.burnoutRate > 10 ? "text-yellow-600" : "text-green-600"
                        }`}>
                          {scheme.burnoutRate}%
                        </div>
                        <p className="text-xs text-gray-600">Burnout Rate</p>
                      </div>
                    </div>

                    <Separator className="my-4" />

                    <div className="flex items-center justify-between">
                      <div>
                        <span className="text-sm text-gray-600">Average Cost per Member:</span>
                        <div className="font-semibold">KES {scheme.averageCostPerMember.toLocaleString()}</div>
                      </div>
                      <Button variant="outline" size="sm">
                        Configure Benefits
                      </Button>
                    </div>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="appeals" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <AlertTriangle className="w-5 h-5 mr-2" />
                Claims Appeals Management
              </CardTitle>
              <CardDescription>
                Review and process member appeals for denied claims
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="text-center py-12">
                <AlertTriangle className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-600">No pending appeals</p>
                <p className="text-sm text-gray-500">Member appeals will appear here for review</p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {/* Footer */}
      <div className="text-center text-sm text-gray-500 mt-8">
        Powered by Aboolean
      </div>
    </div>
  );
}

