      <main className="flex-1 p-6 space-y-6 overflow-y-auto">
        <WelcomeCard user={dashboardUser} />
        <QuickStats user={dashboardUser} />
        <RecentClaims />
        <QuickActions user={dashboardUser} />
      </main>
    </div>
  );
}

