  const [, setLocation] = useLocation();
  const prescriptions = [
    {
      id: "RX-2024-001",
      patient: "John Kamau",
      medication: "Amoxicillin 500mg",
      doctor: "Dr. Sarah Wanjiru",
      status: "validated",
      insurer: "SHA",
      time: "10 mins ago"
    },
    {
      id: "RX-2024-002",
      patient: "Mary Njeri",
      medication: "Metformin 850mg + Glimepiride 2mg",
      doctor: "Dr. James Mwangi",
      status: "pending",
      insurer: "CIC",
      time: "25 mins ago"
    },
    {
      id: "RX-2024-003",
