import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { 
  MessageSquare, 
  Clock, 
  CheckCircle, 
  AlertTriangle, 
  Users, 
  TrendingUp, 
  Filter,
  Search,
  MoreHorizontal,
  Eye,
  MessageCircle,
  FileText,
  Calendar
} from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";

interface SupportTicket {
  id: string;
  user_id: string;
  user_role: string;
  title: string;
  description: string;
  category: string;
  priority: 'low' | 'medium' | 'high' | 'urgent';
  status: 'open' | 'in_progress' | 'resolved' | 'closed';
  created_at: string;
  updated_at: string;
  resolved_at?: string;
  assigned_to?: string;
}

interface TicketResponse {
  id: string;
  ticket_id: string;
  responder_id: string;
  responder_name: string;
  message: string;
  created_at: string;
}

export default function SupportDashboard() {
  const [selectedTicket, setSelectedTicket] = useState<SupportTicket | null>(null);
  const [filterStatus, setFilterStatus] = useState<string>("all");
  const [filterPriority, setFilterPriority] = useState<string>("all");
  const [searchQuery, setSearchQuery] = useState("");
  const [responseMessage, setResponseMessage] = useState("");

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch tickets
  const { data: tickets = [], isLoading } = useQuery({
    queryKey: ["/api/support/tickets", filterStatus, filterPriority],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (filterStatus !== "all") params.append("status", filterStatus);
      const response = await fetch(`http://localhost:8002/tickets?${params.toString()}`);
      return response.json();
    }
  });

  // Fetch ticket responses
  const { data: responses = [] } = useQuery({
    queryKey: ["/api/support/responses", selectedTicket?.id],
    queryFn: async () => {
      if (!selectedTicket?.id) return [];
      const response = await fetch(`http://localhost:8002/tickets/${selectedTicket.id}/responses`);
      return response.json();
    },
    enabled: !!selectedTicket?.id
  });

  // Update ticket mutation
  const updateTicketMutation = useMutation({
    mutationFn: async ({ ticketId, update }: { ticketId: string; update: any }) => {
      const response = await fetch(`http://localhost:8002/tickets/${ticketId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(update)
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/support/tickets"] });
      toast({
        title: "Ticket Updated",
        description: "The support ticket has been updated successfully."
      });
    }
  });

  // Filter tickets
  const filteredTickets = tickets.filter((ticket: SupportTicket) => {
    const matchesSearch = ticket.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         ticket.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         ticket.user_role.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus = filterStatus === "all" || ticket.status === filterStatus;
    const matchesPriority = filterPriority === "all" || ticket.priority === filterPriority;

    return matchesSearch && matchesStatus && matchesPriority;
  });

  // Calculate statistics
  const stats = {
    total: tickets.length,
    open: tickets.filter((t: SupportTicket) => t.status === 'open').length,
    inProgress: tickets.filter((t: SupportTicket) => t.status === 'in_progress').length,
    resolved: tickets.filter((t: SupportTicket) => t.status === 'resolved').length,
    urgent: tickets.filter((t: SupportTicket) => t.priority === 'urgent').length
  };

  const handleUpdateTicket = (status: string, priority?: string) => {
    if (!selectedTicket) return;

    const update: any = { status };
    if (priority) update.priority = priority;
    if (responseMessage.trim()) update.response = responseMessage.trim();

    updateTicketMutation.mutate({ ticketId: selectedTicket.id, update });
    setResponseMessage("");
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'bg-red-100 text-red-800 border-red-200';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'open': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'in_progress': return 'bg-purple-100 text-purple-800 border-purple-200';
      case 'resolved': return 'bg-green-100 text-green-800 border-green-200';
      case 'closed': return 'bg-gray-100 text-gray-800 border-gray-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-teal-50 to-blue-50 dark:from-gray-900 dark:to-gray-800 p-4">
      <div className="max-w-7xl mx-auto pt-8">
        {/* Header */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center">
            <div className="bg-teal-100 dark:bg-teal-900 p-3 rounded-full mr-4">
              <MessageSquare className="w-8 h-8 text-teal-600 dark:text-teal-400" />
            </div>
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
                Support Dashboard
              </h1>
              <p className="text-gray-600 dark:text-gray-400">
                Manage support tickets and user assistance
              </p>
            </div>
          </div>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-5 gap-6 mb-8">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Tickets</CardTitle>
              <MessageSquare className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.total}</div>
              <p className="text-xs text-muted-foreground">All time</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Open</CardTitle>
              <Clock className="h-4 w-4 text-blue-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-blue-600">{stats.open}</div>
              <p className="text-xs text-muted-foreground">Awaiting response</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">In Progress</CardTitle>
              <TrendingUp className="h-4 w-4 text-purple-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-purple-600">{stats.inProgress}</div>
              <p className="text-xs text-muted-foreground">Being handled</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Resolved</CardTitle>
              <CheckCircle className="h-4 w-4 text-green-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">{stats.resolved}</div>
              <p className="text-xs text-muted-foreground">This month</p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Urgent</CardTitle>
              <AlertTriangle className="h-4 w-4 text-red-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">{stats.urgent}</div>
              <p className="text-xs text-muted-foreground">Needs attention</p>
            </CardContent>
          </Card>
        </div>

        {/* Filters and Search */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>Support Tickets</CardTitle>
            <CardDescription>Manage and respond to user support requests</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex flex-col md:flex-row gap-4 mb-6">
              <div className="flex-1">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <Input
                    placeholder="Search tickets..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pl-10"
                  />
                </div>
              </div>
              <Select value={filterStatus} onValueChange={setFilterStatus}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="Status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="open">Open</SelectItem>
                  <SelectItem value="in_progress">In Progress</SelectItem>
                  <SelectItem value="resolved">Resolved</SelectItem>
                  <SelectItem value="closed">Closed</SelectItem>
                </SelectContent>
              </Select>
              <Select value={filterPriority} onValueChange={setFilterPriority}>
                <SelectTrigger className="w-40">
                  <SelectValue placeholder="Priority" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Priority</SelectItem>
                  <SelectItem value="urgent">Urgent</SelectItem>
                  <SelectItem value="high">High</SelectItem>
                  <SelectItem value="medium">Medium</SelectItem>
                  <SelectItem value="low">Low</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Tickets Table */}
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Ticket</TableHead>
                  <TableHead>User</TableHead>
                  <TableHead>Category</TableHead>
                  <TableHead>Priority</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Created</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredTickets.map((ticket: SupportTicket) => (
                  <TableRow key={ticket.id}>
                    <TableCell>
                      <div>
                        <p className="font-medium">{ticket.title}</p>
                        <p className="text-sm text-gray-600 line-clamp-1">{ticket.description}</p>
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge variant="outline" className="capitalize">
                        {ticket.user_role}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <Badge variant="secondary" className="capitalize">
                        {ticket.category}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <Badge className={`${getPriorityColor(ticket.priority)} capitalize`}>
                        {ticket.priority}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <Badge className={`${getStatusColor(ticket.status)} capitalize`}>
                        {ticket.status.replace('_', ' ')}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <div className="text-sm text-gray-600">
                        {formatDate(ticket.created_at)}
                      </div>
                    </TableCell>
                    <TableCell>
                      <Dialog>
                        <DialogTrigger asChild>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => setSelectedTicket(ticket)}
                          >
                            <Eye className="h-4 w-4" />
                          </Button>
                        </DialogTrigger>
                        <DialogContent className="max-w-4xl max-h-[80vh] overflow-y-auto">
                          <DialogHeader>
                            <DialogTitle>{selectedTicket?.title}</DialogTitle>
                            <DialogDescription>
                              Ticket #{selectedTicket?.id.slice(-8)} ‚Ä¢ Created {selectedTicket && formatDate(selectedTicket.created_at)}
                            </DialogDescription>
                          </DialogHeader>

                          {selectedTicket && (
                            <div className="space-y-6">
                              {/* Ticket Details */}
                              <div className="grid grid-cols-2 gap-4">
                                <div>
                                  <Label className="text-sm font-medium">User Role</Label>
                                  <Badge variant="outline" className="mt-1 capitalize">
                                    {selectedTicket.user_role}
                                  </Badge>
                                </div>
                                <div>
                                  <Label className="text-sm font-medium">Category</Label>
                                  <Badge variant="secondary" className="mt-1 capitalize">
                                    {selectedTicket.category}
                                  </Badge>
                                </div>
                                <div>
                                  <Label className="text-sm font-medium">Priority</Label>
                                  <Badge className={`mt-1 ${getPriorityColor(selectedTicket.priority)} capitalize`}>
                                    {selectedTicket.priority}
                                  </Badge>
                                </div>
                                <div>
                                  <Label className="text-sm font-medium">Status</Label>
                                  <Badge className={`mt-1 ${getStatusColor(selectedTicket.status)} capitalize`}>
                                    {selectedTicket.status.replace('_', ' ')}
                                  </Badge>
                                </div>
                              </div>

                              {/* Description */}
                              <div>
                                <Label className="text-sm font-medium">Description</Label>
                                <div className="mt-2 p-3 bg-gray-50 rounded-lg text-sm">
                                  {selectedTicket.description}
                                </div>
                              </div>

                              {/* Responses */}
                              <div>
                                <Label className="text-sm font-medium">Conversation</Label>
                                <div className="mt-2 space-y-3 max-h-60 overflow-y-auto">
                                  {responses.map((response: TicketResponse) => (
                                    <div key={response.id} className="flex gap-3">
                                      <div className="w-8 h-8 bg-teal-100 rounded-full flex items-center justify-center text-xs font-medium">
                                        {response.responder_name.charAt(0)}
                                      </div>
                                      <div className="flex-1">
                                        <div className="flex items-center gap-2 mb-1">
                                          <span className="text-sm font-medium">{response.responder_name}</span>
                                          <span className="text-xs text-gray-500">
                                            {formatDate(response.created_at)}
                                          </span>
                                        </div>
                                        <div className="text-sm text-gray-700 bg-white p-2 rounded border">
                                          {response.message}
                                        </div>
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              </div>

                              {/* Response Form */}
                              <div>
                                <Label className="text-sm font-medium">Add Response</Label>
                                <Textarea
                                  placeholder="Type your response..."
                                  value={responseMessage}
                                  onChange={(e) => setResponseMessage(e.target.value)}
                                  className="mt-2"
                                />
                              </div>

                              {/* Actions */}
                              <div className="flex gap-2 pt-4 border-t">
                                <Button
                                  onClick={() => handleUpdateTicket('in_progress')}
                                  disabled={updateTicketMutation.isPending}
                                  variant="outline"
                                >
                                  Mark In Progress
                                </Button>
                                <Button
                                  onClick={() => handleUpdateTicket('resolved')}
                                  disabled={updateTicketMutation.isPending}
                                  className="bg-green-600 hover:bg-green-700"
                                >
                                  Resolve Ticket
                                </Button>
                                <Button
                                  onClick={() => handleUpdateTicket('closed')}
                                  disabled={updateTicketMutation.isPending}
                                  variant="outline"
                                >
                                  Close Ticket
                                </Button>
                              </div>
                            </div>
                          )}
                        </DialogContent>
                      </Dialog>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/verification-page.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Progress } from "@/components/ui/progress";
import { BiometricScanner } from "@/components/biometric-scanner";
import { 
  Fingerprint, Smartphone, Search, User, Calendar, 
  Shield, CheckCircle, Users, CreditCard 
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface PatientData {
  patient: {
    id: number;
    patientId: string;
    firstName: string;
    lastName: string;
    dateOfBirth: string;
    gender: string;
    phoneNumber: string;
    insuranceNumber: string;
    insurancePlan: string;
  };
  benefits: Array<{
    id: number;
    benefitType: string;
    totalAllowed: number;
    used: number;
    remainingAmount: string;
  }>;
  dependents: Array<{
    id: number;
    firstName: string;
    lastName: string;
    relationship: string;
    dateOfBirth: string;
  }>;
}

export default function VerificationPage() {
  const [verificationMethod, setVerificationMethod] = useState<'fingerprint' | 'otp'>('fingerprint');
  const [searchQuery, setSearchQuery] = useState('');
  const [verifiedPatient, setVerifiedPatient] = useState<PatientData | null>(null);
  const [otpCode, setOtpCode] = useState('');
  const queryClient = useQueryClient();

  const verifyPatientMutation = useMutation({
    mutationFn: async (data: { patientId?: string; biometricData?: boolean; otpCode?: string }) => {
      const response = await apiRequest("POST", "/api/patients/verify", data);
      return await response.json();
    },
    onSuccess: (data) => {
      setVerifiedPatient(data);
    },
  });

  const { data: searchResults, isLoading: isSearching } = useQuery({
    queryKey: ["/api/patients/search", { q: searchQuery }],
    enabled: searchQuery.length > 2,
  });

  const handleBiometricVerification = () => {
    verifyPatientMutation.mutate({ 
      patientId: searchQuery,
      biometricData: true 
    });
  };

  const handleOtpVerification = () => {
    verifyPatientMutation.mutate({ 
      patientId: searchQuery,
      otpCode 
    });
  };

  const calculateBenefitUsage = (used: number, total: number) => {
    return Math.round((used / total) * 100);
  };

  const getBenefitColor = (usage: number) => {
    if (usage < 50) return 'bg-green-500';
    if (usage < 80) return 'bg-yellow-500';
    return 'bg-red-500';
  };

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Patient Verification</h1>
          <p className="text-gray-600">Secure biometric and OTP-based patient authentication</p>
        </div>
        <div className="flex items-center space-x-2">
          <Shield className="w-5 h-5 text-teal-600" />
          <span className="text-sm text-gray-600">HIPAA Compliant</span>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Verification Panel */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Fingerprint className="w-5 h-5 text-teal-600" />
              <span>Patient Verification</span>
            </CardTitle>
            <CardDescription>
              Verify patient identity using biometric or OTP methods
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Patient Search */}
            <div className="space-y-2">
              <Label htmlFor="patient-search">Patient ID or Insurance Number</Label>
              <div className="flex space-x-2">
                <div className="relative flex-1">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                  <Input
                    id="patient-search"
                    placeholder="Enter Patient ID, Insurance #, or Name"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pl-10"
                  />
                </div>
                <Button variant="outline" size="icon">
                  <Search className="w-4 h-4" />
                </Button>
              </div>

              {/* Search Results */}
              {isSearching && (
                <div className="p-3 bg-gray-50 rounded-lg">
                  <p className="text-sm text-gray-600">Searching...</p>
                </div>
              )}

              {searchResults && searchResults.length > 0 && (
                <div className="space-y-2">
                  {searchResults.slice(0, 3).map((patient: any) => (
                    <div
                      key={patient.id}
                      className="flex items-center justify-between p-3 bg-gray-50 rounded-lg cursor-pointer hover:bg-gray-100"
                      onClick={() => setSearchQuery(patient.patientId)}
                    >
                      <div>
                        <p className="font-medium">{patient.firstName} {patient.lastName}</p>
                        <p className="text-sm text-gray-500">ID: {patient.patientId}</p>
                      </div>
                      <Badge variant="outline">{patient.insurancePlan}</Badge>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Verification Method Selection */}
            <Tabs value={verificationMethod} onValueChange={(value) => setVerificationMethod(value as 'fingerprint' | 'otp')}>
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="fingerprint" className="flex items-center space-x-2">
                  <Fingerprint className="w-4 h-4" />
                  <span>Biometric</span>
                </TabsTrigger>
                <TabsTrigger value="otp" className="flex items-center space-x-2">
                  <Smartphone className="w-4 h-4" />
                  <span>OTP</span>
                </TabsTrigger>
              </TabsList>

              <TabsContent value="fingerprint" className="space-y-4">
                <Alert>
                  <Fingerprint className="h-4 w-4" />
                  <AlertDescription>
                    Place patient's finger on the biometric scanner for instant verification.
                  </AlertDescription>
                </Alert>

                <BiometricScanner
                  onScanComplete={handleBiometricVerification}
                  isLoading={verifyPatientMutation.isPending}
                />
              </TabsContent>

              <TabsContent value="otp" className="space-y-4">
                <Alert>
                  <Smartphone className="h-4 w-4" />
                  <AlertDescription>
                    A 6-digit OTP will be sent to the patient's registered phone number.
                  </AlertDescription>
                </Alert>

                <div className="space-y-3">
                  <Label htmlFor="otp">Enter 6-digit OTP</Label>
                  <Input
                    id="otp"
                    type="text"
                    maxLength={6}
                    placeholder="123456"
                    value={otpCode}
                    onChange={(e) => setOtpCode(e.target.value.replace(/\D/g, ''))}
                    className="text-center tracking-widest font-mono text-lg"
                  />
                  <div className="flex space-x-2">
                    <Button
                      onClick={handleOtpVerification}
                      disabled={otpCode.length !== 6 || verifyPatientMutation.isPending}
                      className="flex-1"
                    >
                      Verify OTP
                    </Button>
                    <Button variant="outline">
                      Resend
                    </Button>
                  </div>
                </div>
              </TabsContent>
            </Tabs>
          </CardContent>
        </Card>

        {/* Verification Results */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <User className="w-5 h-5 text-blue-600" />
              <span>Patient Information</span>
            </CardTitle>
            <CardDescription>
              Verified patient details and insurance benefits
            </CardDescription>
          </CardHeader>
          <CardContent>
            {!verifiedPatient ? (
              <div className="text-center py-12 text-gray-500">
                <Shield className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                <p>Patient verification required</p>
                <p className="text-sm">Complete biometric or OTP verification to view patient details</p>
              </div>
            ) : (
              <div className="space-y-6">
                {/* Verification Success */}
                <Alert className="border-green-200 bg-green-50">
                  <CheckCircle className="h-4 w-4 text-green-600" />
                  <AlertDescription className="text-green-800">
                    Patient verified successfully
                  </AlertDescription>
                </Alert>

                {/* Patient Profile */}
                <div className="bg-gradient-to-r from-teal-50 to-blue-50 rounded-lg p-4">
                  <div className="flex items-start space-x-4">
                    <div className="w-16 h-16 bg-teal-200 rounded-full flex items-center justify-center">
                      <User className="w-8 h-8 text-teal-700" />
                    </div>
                    <div className="flex-1">
                      <h3 className="text-lg font-semibold text-gray-900">
                        {verifiedPatient.patient.firstName} {verifiedPatient.patient.lastName}
                      </h3>
                      <p className="text-gray-600">ID: {verifiedPatient.patient.patientId}</p>
                      <div className="grid grid-cols-2 gap-4 mt-3 text-sm">
                        <div>
                          <span className="text-gray-500">DOB:</span>
                          <span className="ml-2 font-medium">
                            {new Date(verifiedPatient.patient.dateOfBirth).toLocaleDateString()}
                          </span>
                        </div>
                        <div>
                          <span className="text-gray-500">Gender:</span>
                          <span className="ml-2 font-medium">{verifiedPatient.patient.gender}</span>
                        </div>
                        <div>
                          <span className="text-gray-500">Plan:</span>
                          <span className="ml-2 font-medium">{verifiedPatient.patient.insurancePlan}</span>
                        </div>
                        <div>
                          <span className="text-gray-500">Phone:</span>
                          <span className="ml-2 font-medium">{verifiedPatient.patient.phoneNumber}</span>
                        </div>
                      </div>
                    </div>
                    <Badge className="bg-green-100 text-green-800">Active</Badge>
                  </div>
                </div>

                {/* Benefits Overview */}
                <div className="space-y-4">
                  <h4 className="font-medium text-gray-900 flex items-center space-x-2">
                    <CreditCard className="w-4 h-4" />
                    <span>Insurance Benefits</span>
                  </h4>
                  <div className="grid grid-cols-1 gap-3">
                    {verifiedPatient.benefits.map((benefit) => {
                      const usage = calculateBenefitUsage(benefit.used, benefit.totalAllowed);
                      return (
                        <div key={benefit.id} className="border border-gray-200 rounded-lg p-4">
                          <div className="flex justify-between items-center mb-2">
                            <span className="font-medium capitalize">
                              {benefit.benefitType.replace('-', ' ')}
                            </span>
                            <span className="text-sm text-gray-500">
                              {benefit.used}/{benefit.totalAllowed}
                            </span>
                          </div>
                          <Progress value={usage} className="h-2 mb-2" />
                          <div className="flex justify-between text-sm">
                            <span className="text-gray-500">Usage: {usage}%</span>
                            {benefit.remainingAmount && (
                              <span className="text-gray-500">
                                Remaining: ${benefit.remainingAmount}
                              </span>
                            )}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>

                {/* Dependents */}
                {verifiedPatient.dependents.length > 0 && (
                  <div className="space-y-4">
                    <h4 className="font-medium text-gray-900 flex items-center space-x-2">
                      <Users className="w-4 h-4" />
                      <span>Dependents</span>
                    </h4>
                    <div className="space-y-2">
                      {verifiedPatient.dependents.map((dependent) => (
                        <div key={dependent.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                          <div>
                            <p className="font-medium">
                              {dependent.firstName} {dependent.lastName}
                            </p>
                            <p className="text-sm text-gray-500">
                              {dependent.relationship} ‚Ä¢ Born {new Date(dependent.dateOfBirth).toLocaleDateString()}
                            </p>
                          </div>
                          <Badge variant="outline">Covered</Badge>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Action Button */}
                <Button 
                  className="w-full bg-teal-600 hover:bg-teal-700"
                  onClick={() => window.location.href = '/claims'}
                >
                  Proceed to Claims Processing
                </Button>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/utils/autofillClaim.ts.content:
import { PDFDocument, PDFTextField, PDFCheckBox } from "pdf-lib";

export interface PatientData {
  name: string;
  dateOfBirth: string;
  gender: string;
  memberId: string;
  phoneNumber?: string;
  address?: string;
  diagnosis?: string;
  treatment?: string;
  serviceDate?: string;
  providerName?: string;
  totalAmount?: string;
}

export interface ClaimService {
  serviceCode: string;
  serviceName: string;
  quantity: number;
  unitCost: number;
  totalCost: number;
  serviceDate: string;
}

export async function generateClaimForm(
  patientData: PatientData, 
  insurer: string,
  services: ClaimService[] = []
): Promise<Uint8Array> {
  try {
    // Create a new PDF document for claim forms
    const pdfDoc = await PDFDocument.create();
    const page = pdfDoc.addPage([595, 842]); // A4 size

    const { width, height } = page.getSize();
    const fontSize = 10;
    const titleFontSize = 14;
    const headerFontSize = 12;

    // Header with insurer branding
    const headerY = height - 50;
    page.drawText(`${insurer.toUpperCase()} MEDICAL CLAIM FORM`, {
      x: 50,
      y: headerY,
      size: titleFontSize,
    });

    page.drawText(`Claim Date: ${new Date().toLocaleDateString()}`, {
      x: width - 200,
      y: headerY,
      size: fontSize,
    });

    // Patient Information Section
    let currentY = headerY - 60;
    page.drawText('PATIENT INFORMATION', {
      x: 50,
      y: currentY,
      size: headerFontSize,
    });

    currentY -= 30;
    const leftColumn = 50;
    const rightColumn = 300;

    // Patient details
    page.drawText(`Name: ${patientData.name}`, {
      x: leftColumn,
      y: currentY,
      size: fontSize,
    });

    page.drawText(`Member ID: ${patientData.memberId}`, {
      x: rightColumn,
      y: currentY,
      size: fontSize,
    });

    currentY -= 20;
    page.drawText(`Date of Birth: ${patientData.dateOfBirth}`, {
      x: leftColumn,
      y: currentY,
      size: fontSize,
    });

    page.drawText(`Gender: ${patientData.gender}`, {
      x: rightColumn,
      y: currentY,
      size: fontSize,
    });

    if (patientData.phoneNumber) {
      currentY -= 20;
      page.drawText(`Phone: ${patientData.phoneNumber}`, {
        x: leftColumn,
        y: currentY,
        size: fontSize,
      });
    }

    if (patientData.address) {
      page.drawText(`Address: ${patientData.address}`, {
        x: rightColumn,
        y: currentY,
        size: fontSize,
      });
    }

    // Medical Information Section
    currentY -= 50;
    page.drawText('MEDICAL INFORMATION', {
      x: 50,
      y: currentY,
      size: headerFontSize,
    });

    currentY -= 30;
    if (patientData.diagnosis) {
      page.drawText(`Diagnosis: ${patientData.diagnosis}`, {
        x: leftColumn,
        y: currentY,
        size: fontSize,
      });
      currentY -= 20;
    }

    if (patientData.treatment) {
      page.drawText(`Treatment: ${patientData.treatment}`, {
        x: leftColumn,
        y: currentY,
        size: fontSize,
      });
      currentY -= 20;
    }

    if (patientData.serviceDate) {
      page.drawText(`Service Date: ${patientData.serviceDate}`, {
        x: leftColumn,
        y: currentY,
        size: fontSize,
      });
      currentY -= 20;
    }

    // Services Table
    if (services.length > 0) {
      currentY -= 30;
      page.drawText('SERVICES PROVIDED', {
        x: 50,
        y: currentY,
        size: headerFontSize,
      });

      currentY -= 30;
      // Table header
      page.drawText('Service Code', { x: 50, y: currentY, size: fontSize });
      page.drawText('Description', { x: 130, y: currentY, size: fontSize });
      page.drawText('Qty', { x: 300, y: currentY, size: fontSize });
      page.drawText('Unit Cost', { x: 350, y: currentY, size: fontSize });
      page.drawText('Total', { x: 420, y: currentY, size: fontSize });
      page.drawText('Date', { x: 480, y: currentY, size: fontSize });

      // Draw line under header
      currentY -= 5;
      page.drawLine({
        start: { x: 50, y: currentY },
        end: { x: 550, y: currentY },
        thickness: 1,
      });

      // Service rows
      let totalAmount = 0;
      services.forEach((service) => {
        currentY -= 20;
        page.drawText(service.serviceCode, { x: 50, y: currentY, size: fontSize });
        page.drawText(service.serviceName.substring(0, 20), { x: 130, y: currentY, size: fontSize });
        page.drawText(service.quantity.toString(), { x: 300, y: currentY, size: fontSize });
        page.drawText(`KES ${service.unitCost.toFixed(2)}`, { x: 350, y: currentY, size: fontSize });
        page.drawText(`KES ${service.totalCost.toFixed(2)}`, { x: 420, y: currentY, size: fontSize });
        page.drawText(service.serviceDate, { x: 480, y: currentY, size: fontSize });
        totalAmount += service.totalCost;
      });

      // Total line
      currentY -= 25;
      page.drawLine({
        start: { x: 350, y: currentY + 15 },
        end: { x: 480, y: currentY + 15 },
        thickness: 1,
      });
      page.drawText(`TOTAL: KES ${totalAmount.toFixed(2)}`, {
        x: 420,
        y: currentY,
        size: headerFontSize,
      });
    }

    // Provider Information
    currentY -= 50;
    page.drawText('PROVIDER INFORMATION', {
      x: 50,
      y: currentY,
      size: headerFontSize,
    });

    currentY -= 30;
    if (patientData.providerName) {
      page.drawText(`Provider: ${patientData.providerName}`, {
        x: leftColumn,
        y: currentY,
        size: fontSize,
      });
    }

    page.drawText(`Facility: Erlessed Healthcare Platform`, {
      x: rightColumn,
      y: currentY,
      size: fontSize,
    });

    // Signature section
    currentY -= 50;
    page.drawText('SIGNATURES', {
      x: 50,
      y: currentY,
      size: headerFontSize,
    });

    currentY -= 40;
    page.drawText('Patient/Guardian Signature: ________________________', {
      x: 50,
      y: currentY,
      size: fontSize,
    });

    page.drawText('Date: __________', {
      x: 400,
      y: currentY,
      size: fontSize,
    });

    currentY -= 30;
    page.drawText('Provider Signature: ____________________________', {
      x: 50,
      y: currentY,
      size: fontSize,
    });

    page.drawText('Date: __________', {
      x: 400,
      y: currentY,
      size: fontSize,
    });

    // Footer
    page.drawText(`Generated by Erlessed Healthcare Platform - ${new Date().toISOString()}`, {
      x: 50,
      y: 30,
      size: 8,
    });

    // Insurer-specific customizations
    if (insurer.toLowerCase() === 'sha') {
      page.drawText('SHA Member Benefits Apply', {
        x: width - 200,
        y: 50,
        size: fontSize,
      });
    } else if (insurer.toLowerCase() === 'cic') {
      page.drawText('CIC Insurance Policy Coverage', {
        x: width - 200,
        y: 50,
        size: fontSize,
      });
    } else if (insurer.toLowerCase() === 'aar') {
      page.drawText('AAR Insurance Medical Cover', {
        x: width - 200,
        y: 50,
        size: fontSize,
      });
    }

    return await pdfDoc.save();
  } catch (error) {
    console.error('Error generating claim form:', error);
    throw new Error('Failed to generate claim form');
  }
}

// Helper function to download the generated PDF
export function downloadPDF(pdfBytes: Uint8Array, filename: string) {
  const blob = new Blob([pdfBytes], { type: 'application/pdf' });
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.href = url;
  link.download = filename;
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
  URL.revokeObjectURL(url);
}

// Generate filename based on patient and insurer data
export function generateFilename(patientData: PatientData, insurer: string): string {
  const date = new Date().toISOString().split('T')[0];
  const sanitizedName = patientData.name.replace(/[^a-zA-Z0-9]/g, '_');
  return `${insurer}_Claim_${sanitizedName}_${date}.pdf`;
}

